/**
 * Monetization Dashboard API
 * Complete view of earnings, subscriptions, affiliates, add-ons
 */

import { NextRequest, NextResponse } from 'next/server';
import { getUserFromRequest } from '@/lib/firebase-server';
import { captureError } from '@/lib/sentry';
import {
  getUserSubscription,
  getTierDetails,
  getTeamMembers,
} from '@/lib/subscription-manager';
import {
  getAffiliateProfile,
  getAffiliateStats,
  getAffiliateReferrals,
} from '@/lib/affiliate-system';
import {
  getWhiteLabelConfig,
} from '@/lib/white-label';
import {
  getRevenueMetricsRange,
} from '@/lib/advanced-analytics';

export const runtime = 'nodejs';
export const dynamic = 'force-dynamic';

/**
 * GET /api/monetization/dashboard
 * Complete monetization overview
 */
export async function GET(request: NextRequest) {
  try {
    const user = await getUserFromRequest(request);
    if (!user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    // Get subscription info
    const subscription = await getUserSubscription(user.uid);
    const tier = subscription ? getTierDetails(subscription.tier) : getTierDetails('free');

    // Get team info
    const teamMembers = await getTeamMembers(user.uid);
    const activeMembers = teamMembers.filter(m => m.isActive).length;

    // Get affiliate info
    const affiliateProfile = await getAffiliateProfile(user.uid);
    const affiliateStats = affiliateProfile ? await getAffiliateStats(user.uid) : null;

    // Get white-label config
    const whiteLabelConfig = await getWhiteLabelConfig(user.uid);

    // Get revenue metrics for current month
    const today = new Date();
    const currentMonth = today.toISOString().slice(0, 7);
    const lastMonth = new Date(today.getFullYear(), today.getMonth() - 1, 1)
      .toISOString()
      .slice(0, 7);

    const revenueData = await getRevenueMetricsRange(
      user.uid,
      lastMonth,
      currentMonth
    );
    const currentMonthRevenue = revenueData.find(r => r.month === currentMonth);
    const lastMonthRevenue = revenueData.find(r => r.month === lastMonth);

    const mrrGrowth =
      lastMonthRevenue && currentMonthRevenue
        ? (
            ((currentMonthRevenue.mrr - lastMonthRevenue.mrr) /
              Math.max(1, lastMonthRevenue.mrr)) *
            100
          ).toFixed(1)
        : '0';

    return NextResponse.json({
      success: true,
      dashboard: {
        subscriptionStatus: {
          tier: tier.name,
          description: tier.description,
          price: tier.price > 0 ? `$${tier.price}/month` : 'Free',
          users: `${activeMembers}/${tier.users}`,
          storage: `${subscription?.storageUsed || 0}GB / ${tier.storage}GB`,
          nextBillingDate: subscription?.nextBillingDate?.toISOString(),
          autoRenew: subscription?.autoRenew ?? true,
          status: subscription?.status || 'inactive',
        },
        team: {
          totalMembers: activeMembers,
          maxMembers: tier.users,
          members: teamMembers
            .filter(m => m.isActive)
            .map(m => ({
              name: m.name || m.email,
              email: m.email,
              role: m.role,
              joinedAt: m.joinedAt.toISOString(),
            })),
        },
        affiliate: affiliateProfile
          ? {
              status: 'active',
              referralCode: affiliateProfile.referralCode,
              referralLink: affiliateProfile.referralLink,
              tier: affiliateProfile.tier,
              commissionRate: `${(affiliateProfile.commissionRate * 100).toFixed(0)}%`,
              totalEarnings: `$${affiliateProfile.totalEarnings.toFixed(2)}`,
              monthlyEarnings: `$${affiliateProfile.monthlyEarnings.toFixed(2)}`,
              totalReferrals: affiliateStats?.totalReferrals || 0,
              activeReferrals: affiliateStats?.activeReferrals || 0,
              conversionRate: `${(affiliateStats?.conversionRate || 0).toFixed(1)}%`,
            }
          : {
              status: 'inactive',
              message: 'Not enrolled in affiliate program',
            },
        whiteLabelFeatures: whiteLabelConfig
          ? {
              status: 'active',
              companyName: whiteLabelConfig.companyName,
              customDomain: whiteLabelConfig.customDomain,
              primaryColor: whiteLabelConfig.primaryColor,
              features: Object.entries(whiteLabelConfig.features)
                .filter(([_, enabled]) => enabled)
                .map(([feature]) => feature),
            }
          : {
              status: 'inactive',
              message: 'White-label features not configured',
            },
        revenue: {
          currentMonthMRR: `$${(currentMonthRevenue?.mrr || 0).toFixed(2)}`,
          lastMonthMRR: `$${(lastMonthRevenue?.mrr || 0).toFixed(2)}`,
          growth: `${mrrGrowth}%`,
          totalRevenue: `$${(currentMonthRevenue?.totalRevenue || 0).toFixed(2)}`,
          subscriptionRevenue: `$${(
            currentMonthRevenue?.subscriptionRevenue || 0
          ).toFixed(2)}`,
          affiliateRevenue: `$${(currentMonthRevenue?.affiliateRevenue || 0).toFixed(2)}`,
          addonRevenue: `$${(currentMonthRevenue?.addonRevenue || 0).toFixed(2)}`,
          churnRate: `${(currentMonthRevenue?.churnRate || 0).toFixed(1)}%`,
        },
        features: {
          customization: {
            whiteLabel: tier.features.includes('Custom integrations'),
            customDomain: whiteLabelConfig?.customDomain !== undefined,
            teamCollaboration: tier.users > 1,
            advancedReporting: tier.features.includes('Advanced reporting'),
            apiAccess: tier.features.includes('API access'),
          },
          limits: {
            aiGenerations: tier.limits.aiGenerations === -1 ? 'Unlimited' : tier.limits.aiGenerations,
            dmReplies: tier.limits.dmReplies === -1 ? 'Unlimited' : tier.limits.dmReplies,
            moneyPlays: tier.limits.moneyPlays === -1 ? 'Unlimited' : tier.limits.moneyPlays,
            imageGenerations: tier.limits.imageGenerations === -1 ? 'Unlimited' : tier.limits.imageGenerations,
            videoGenerations: tier.limits.videoGenerations === -1 ? 'Unlimited' : tier.limits.videoGenerations,
          },
        },
      },
    });
  } catch (error) {
    captureError(error, { context: 'api/monetization/dashboard' });
    return NextResponse.json(
      { error: 'Failed to fetch monetization dashboard' },
      { status: 500 }
    );
  }
}

/**
 * POST /api/monetization/upgrade
 * Upgrade subscription tier
 */
export async function POST(request: NextRequest) {
  try {
    const user = await getUserFromRequest(request);
    if (!user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    const body = await request.json();
    const { targetTier } = body;

    if (!targetTier) {
      return NextResponse.json({ error: 'Target tier is required' }, { status: 400 });
    }

    // TODO: Create Stripe checkout session for upgrade
    // This would integrate with the Stripe billing module

    return NextResponse.json({
      success: true,
      message: `Upgrade to ${targetTier} initiated`,
      checkoutUrl: `/checkout?tier=${targetTier}`,
    });
  } catch (error) {
    captureError(error, { context: 'api/monetization/upgrade' });
    return NextResponse.json(
      { error: 'Failed to initiate upgrade' },
      { status: 500 }
    );
  }
}

'use client';

import React, { useState, useEffect } from 'react';
import { Play, Plus, Search, Filter, Clock, Star, Download } from 'lucide-react';
import DashboardLayout from '@/components/DashboardLayout';

interface MediaItem {
  id: string;
  title: string;
  poster: string;
  type: 'movie' | 'series' | 'music' | 'photo';
  progress: number; // 0-100
  duration: number; // in minutes
  source: 'youtube' | 'plex' | 'jellyfin' | 'drive' | 'tmdb';
  rating: number;
}

const SAMPLE_MEDIA: MediaItem[] = [
  {
    id: '1',
    title: 'Inception',
    poster: 'https://images.unsplash.com/photo-1574375927938-d5a98e8ffe85?w=200&h=300',
    type: 'movie',
    progress: 65,
    duration: 148,
    source: 'plex',
    rating: 8.8,
  },
  {
    id: '2',
    title: 'The Matrix',
    poster: 'https://images.unsplash.com/photo-1533613220915-609f21a91335?w=200&h=300',
    type: 'movie',
    progress: 100,
    duration: 136,
    source: 'plex',
    rating: 8.7,
  },
  {
    id: '3',
    title: 'Stranger Things',
    poster: 'https://images.unsplash.com/photo-1598899134739-24c46f58b8c0?w=200&h=300',
    type: 'series',
    progress: 45,
    duration: 45,
    source: 'jellyfin',
    rating: 8.9,
  },
];

export default function MediaHubPage() {
  const [media, setMedia] = useState<MediaItem[]>(SAMPLE_MEDIA);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterType, setFilterType] = useState<'all' | 'movie' | 'series' | 'music' | 'photo'>('all');
  const [isMounted, setIsMounted] = useState(false);

  useEffect(() => {
    setIsMounted(true);
  }, []);

  const filteredMedia = media.filter((item) => {
    const matchesSearch = item.title.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesType = filterType === 'all' || item.type === filterType;
    return matchesSearch && matchesType;
  });

  if (!isMounted) return null;

  return (
    <DashboardLayout>
      <div className="min-h-screen bg-gradient-to-br from-black via-gray-900 to-black">
        {/* Header */}
        <div className="border-b border-cyan-500/20 bg-black/80 backdrop-blur-md sticky top-0 z-40">
          <div className="max-w-7xl mx-auto px-6 py-6">
            <h1 className="text-3xl font-bold bg-gradient-to-r from-cyan-400 to-purple-400 bg-clip-text text-transparent mb-6">
               MediaHub
            </h1>

            {/* Search & Filters */}
            <div className="space-y-4">
              <div className="flex gap-3">
                <div className="flex-1 relative">
                  <Search className="absolute left-3 top-3 text-gray-400 pointer-events-none" size={20} />
                  <input
                    type="text"
                    placeholder="Search movies, series, music..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="w-full bg-black/50 border border-cyan-500/30 rounded-lg pl-10 pr-4 py-2 text-cyan-300 placeholder-gray-500 focus:outline-none focus:border-cyan-500"
                  />
                </div>
                <button className="px-4 py-2 bg-cyan-500/20 hover:bg-cyan-500/30 border border-cyan-500/30 rounded-lg transition-colors flex items-center gap-2 text-cyan-300">
                  <Filter size={20} />
                  Filters
                </button>
              </div>

              {/* Type Filter */}
              <div className="flex gap-2 overflow-x-auto pb-2">
                {(['all', 'movie', 'series', 'music', 'photo'] as const).map((type) => (
                  <button
                    key={type}
                    onClick={() => setFilterType(type)}
                    className={`px-4 py-2 rounded-lg transition-colors whitespace-nowrap text-sm font-semibold ${
                      filterType === type
                        ? 'bg-cyan-500/30 border border-cyan-500 text-cyan-300'
                        : 'bg-black/50 border border-cyan-500/20 text-gray-300 hover:border-cyan-500/40'
                    }`}
                  >
                    {type.charAt(0).toUpperCase() + type.slice(1)}
                  </button>
                ))}
              </div>
            </div>
          </div>
        </div>

        {/* Continue Watching */}
        <div className="max-w-7xl mx-auto px-6 py-8">
          <div className="mb-12">
            <h2 className="text-2xl font-bold text-cyan-300 mb-6 flex items-center gap-2">
              <Clock size={24} />
              Continue Watching
            </h2>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {filteredMedia
                .filter((m) => m.progress > 0 && m.progress < 100)
                .map((item) => (
                  <MediaCard key={item.id} item={item} />
                ))}
            </div>
          </div>

          {/* Trending */}
          <div className="mb-12">
            <h2 className="text-2xl font-bold text-purple-300 mb-6 flex items-center gap-2">
              <Star size={24} />
              Trending Now
            </h2>
            <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-5 gap-4">
              {filteredMedia.map((item) => (
                <div
                  key={item.id}
                  className="group cursor-pointer relative rounded-lg overflow-hidden"
                >
                  <img
                    src={item.poster}
                    alt={item.title}
                    className="w-full h-48 object-cover group-hover:scale-110 transition-transform duration-300"
                  />
                  <div className="absolute inset-0 bg-black/60 opacity-0 group-hover:opacity-100 transition-opacity flex items-center justify-center">
                    <Play className="text-white fill-white" size={40} />
                  </div>
                  <div className="absolute bottom-0 left-0 right-0 bg-gradient-to-t from-black to-transparent p-2">
                    <p className="text-xs font-semibold text-white truncate">{item.title}</p>
                    <p className="text-xs text-gray-300">{item.source}</p>
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Your Library */}
          <div>
            <h2 className="text-2xl font-bold text-green-300 mb-6 flex items-center gap-2">
              <Download size={24} />
              Your Library
            </h2>
            <div className="grid grid-cols-1 gap-4">
              {filteredMedia.map((item) => (
                <div
                  key={item.id}
                  className="flex gap-4 p-4 bg-black/50 border border-cyan-500/10 rounded-lg hover:border-cyan-500/30 transition-colors group cursor-pointer"
                >
                  <img
                    src={item.poster}
                    alt={item.title}
                    className="w-32 h-48 object-cover rounded group-hover:scale-105 transition-transform"
                  />
                  <div className="flex-1 flex flex-col justify-between py-2">
                    <div>
                      <h3 className="text-lg font-semibold text-cyan-300">{item.title}</h3>
                      <p className="text-sm text-gray-400 mt-1">
                        {item.type.charAt(0).toUpperCase() + item.type.slice(1)}  {item.duration} min
                      </p>
                      <div className="flex items-center gap-3 mt-3">
                        <div className="flex-1 max-w-xs bg-gray-700 h-1 rounded-full overflow-hidden">
                          { }
                          <div
                            className="bg-gradient-to-r from-cyan-500 to-purple-500 h-full"
                            style={{ width: `${item.progress}%` }}
                          />
                        </div>
                        <span className="text-xs text-gray-400">{item.progress}%</span>
                      </div>
                      <p className="text-sm text-yellow-400 mt-2"> {item.rating}/10</p>
                    </div>
                    <div className="flex gap-2">
                      <button className="px-4 py-2 bg-cyan-500/20 hover:bg-cyan-500/30 border border-cyan-500/30 rounded transition-colors text-sm font-semibold text-cyan-300 flex items-center gap-2">
                        <Play size={16} />
                        Play
                      </button>
                      <button className="px-4 py-2 bg-gray-700/20 hover:bg-gray-700/30 border border-gray-600/30 rounded transition-colors text-sm font-semibold text-gray-300">
                        Details
                      </button>
                    </div>
                  </div>
                  <div className="text-xs text-gray-500 py-2">{item.source.toUpperCase()}</div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </DashboardLayout>
  );
}

interface MediaCardProps {
  item: MediaItem;
}

function MediaCard({ item }: MediaCardProps) {
  return (
    <div className="group cursor-pointer rounded-lg overflow-hidden bg-black/50 border border-cyan-500/10 hover:border-cyan-500/30 transition-colors">
      <div className="relative overflow-hidden h-64">
        <img
          src={item.poster}
          alt={item.title}
          className="w-full h-full object-cover group-hover:scale-110 transition-transform duration-300"
        />
        <div className="absolute inset-0 bg-black/40 opacity-0 group-hover:opacity-100 transition-opacity flex items-center justify-center">
          <Play className="text-white fill-white" size={48} />
        </div>
        <div className="absolute top-2 right-2 bg-black/80 px-2 py-1 rounded text-xs font-semibold text-yellow-400">
           {item.rating}
        </div>
      </div>
      <div className="p-4">
        <h3 className="font-semibold text-cyan-300 mb-2">{item.title}</h3>
        <div className="space-y-2">
          <div className="bg-gray-700 h-1 rounded-full overflow-hidden">
            <div
              className="bg-gradient-to-r from-cyan-500 to-purple-500 h-full"
              style={{ width: `${item.progress}%` }}
            />
          </div>
          <div className="flex justify-between text-xs text-gray-400">
            <span>{item.progress}% watched</span>
            <span>{item.source}</span>
          </div>
        </div>
      </div>
    </div>
  );
}

'use client';

import React, { useState, useEffect } from 'react';
import { Wallet, TrendingUp, Send, ArrowUpRight, Eye, EyeOff, Plus, RefreshCw } from 'lucide-react';
import DashboardLayout from '@/components/DashboardLayout';

interface TokenBalance {
  symbol: string;
  name: string;
  balance: number;
  usdValue: number;
  change24h: number;
  icon: string;
}

interface WalletAccount {
  address: string;
  network: 'ethereum' | 'polygon' | 'arbitrum';
  balance: number;
  nftCount: number;
}

const SAMPLE_TOKENS: TokenBalance[] = [
  {
    symbol: "ETH",
    name: "Ethereum",
    balance: 2.5,
    usdValue: 4850,
    change24h: 5.2,
    icon: "E",
  },
  {
    symbol: 'BTC',
    name: 'Bitcoin',
    balance: 0.15,
    usdValue: 6000,
    change24h: -2.1,
    icon: '',
  },
  {
    symbol: 'MATIC',
    name: 'Polygon',
    balance: 500,
    usdValue: 250,
    change24h: 8.3,
    icon: '',
  },
  {
    symbol: 'USDC',
    name: 'USD Coin',
    balance: 1200,
    usdValue: 1200,
    change24h: 0,
    icon: '$',
  },
];

const SAMPLE_ACCOUNTS: WalletAccount[] = [
  {
    address: '0x1234...5678',
    network: 'ethereum',
    balance: 12100,
    nftCount: 24,
  },
  {
    address: '0x8765...4321',
    network: 'polygon',
    balance: 450,
    nftCount: 8,
  },
];

export default function Web3Page() {
  const [tokens, setTokens] = useState<TokenBalance[]>(SAMPLE_TOKENS);
  const [accounts, setAccounts] = useState<WalletAccount[]>(SAMPLE_ACCOUNTS);
  const [hideBalance, setHideBalance] = useState(false);
  const [isRefreshing, setIsRefreshing] = useState(false);
  const [activeTab, setActiveTab] = useState<'portfolio' | 'swap' | 'nfts'>('portfolio');
  const [isMounted, setIsMounted] = useState(false);

  useEffect(() => {
    setIsMounted(true);
  }, []);

  const totalBalance = tokens.reduce((sum, token) => sum + token.usdValue, 0);
  const change24h = (
    tokens.reduce((sum, token) => sum + (token.usdValue * token.change24h) / 100, 0) / totalBalance
  ) * 100;

  const handleRefresh = async () => {
    setIsRefreshing(true);
    // Simulate refresh
    await new Promise((resolve) => setTimeout(resolve, 1000));
    setIsRefreshing(false);
  };

  if (!isMounted) return null;

  return (
    <DashboardLayout>
      <div className="min-h-screen bg-gradient-to-br from-black via-gray-900 to-black">
        {/* Header */}
        <div className="border-b border-cyan-500/20 bg-black/80 backdrop-blur-md sticky top-0 z-40">
          <div className="max-w-7xl mx-auto px-6 py-6">
            <h1 className="text-3xl font-bold bg-gradient-to-r from-cyan-400 to-purple-400 bg-clip-text text-transparent mb-4 flex items-center gap-2">
              <Wallet size={32} />
              Web3 Universe
            </h1>

            {/* Portfolio Overview */}
            <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
              <div className="bg-black/50 border border-cyan-500/20 rounded-lg p-4">
                <p className="text-xs text-gray-400 mb-1">Total Balance</p>
                <div className="flex items-baseline gap-2">
                  <p className="text-2xl font-bold text-cyan-300">
                    {hideBalance ? '' : `$${totalBalance.toLocaleString('en-US', { maximumFractionDigits: 0 })}`}
                  </p>
                  <button onClick={() => setHideBalance(!hideBalance)} className="p-1 hover:bg-cyan-500/10 rounded">
                    {hideBalance ? <EyeOff size={16} className="text-gray-400" /> : <Eye size={16} className="text-gray-400" />}
                  </button>
                </div>
              </div>

              <div className="bg-black/50 border border-cyan-500/20 rounded-lg p-4">
                <p className="text-xs text-gray-400 mb-1">24h Change</p>
                <p className={`text-2xl font-bold ${change24h >= 0 ? 'text-green-400' : 'text-red-400'}`}>
                  {change24h >= 0 ? '+' : ''}{change24h.toFixed(2)}%
                </p>
              </div>

              <div className="bg-black/50 border border-cyan-500/20 rounded-lg p-4">
                <p className="text-xs text-gray-400 mb-1">Connected Wallets</p>
                <p className="text-2xl font-bold text-purple-300">{accounts.length}</p>
              </div>

              <div className="bg-black/50 border border-cyan-500/20 rounded-lg p-4">
                <p className="text-xs text-gray-400 mb-1">Total NFTs</p>
                <p className="text-2xl font-bold text-pink-300">
                  {accounts.reduce((sum, acc) => sum + acc.nftCount, 0)}
                </p>
              </div>
            </div>

            {/* Tabs */}
            <div className="flex gap-4 mt-6 border-b border-cyan-500/10">
              {(['portfolio', 'swap', 'nfts'] as const).map((tab) => (
                <button
                  key={tab}
                  onClick={() => setActiveTab(tab)}
                  className={`pb-3 px-2 transition-colors font-semibold text-sm ${
                    activeTab === tab
                      ? 'border-b-2 border-cyan-500 text-cyan-400'
                      : 'text-gray-400 hover:text-gray-300'
                  }`}
                >
                  {tab.charAt(0).toUpperCase() + tab.slice(1)}
                </button>
              ))}
            </div>
          </div>
        </div>

        <div className="max-w-7xl mx-auto px-6 py-8">
          {/* Portfolio Tab */}
          {activeTab === 'portfolio' && (
            <div className="space-y-8">
              {/* Token Holdings */}
              <div>
                <div className="flex items-center justify-between mb-6">
                  <h2 className="text-2xl font-bold text-cyan-300">Token Holdings</h2>
                  <button
                    onClick={handleRefresh}
                    disabled={isRefreshing}
                    title="Refresh token holdings"
                    aria-label="Refresh token holdings"
                    className="p-2 hover:bg-cyan-500/20 rounded-lg transition-colors disabled:opacity-50"
                  >
                    <RefreshCw size={20} className={`text-cyan-400 ${isRefreshing ? 'animate-spin' : ''}`} />
                  </button>
                </div>

                <div className="space-y-3">
                  {tokens.map((token) => (
                    <div
                      key={token.symbol}
                      className="flex items-center justify-between p-4 bg-black/50 border border-cyan-500/10 rounded-lg hover:border-cyan-500/30 transition-colors"
                    >
                      <div className="flex items-center gap-4">
                        <div className="w-12 h-12 rounded-full bg-gradient-to-br from-cyan-500 to-purple-500 flex items-center justify-center text-xl font-bold text-white">
                          {token.icon}
                        </div>
                        <div>
                          <p className="font-semibold text-cyan-300">{token.name}</p>
                          <p className="text-sm text-gray-400">{token.balance} {token.symbol}</p>
                        </div>
                      </div>

                      <div className="flex items-center gap-8">
                        <div className="text-right">
                          <p className="font-semibold text-cyan-300">
                            {hideBalance ? '' : `$${token.usdValue.toLocaleString('en-US')}`}
                          </p>
                          <p className={`text-sm ${token.change24h >= 0 ? 'text-green-400' : 'text-red-400'}`}>
                            {token.change24h >= 0 ? '+' : ''}{token.change24h.toFixed(2)}%
                          </p>
                        </div>

                        <div className="flex gap-2">
                          <button 
                            title="Send"
