'use client';

import { useState, useRef, useEffect } from 'react';
import { Loader2, Mic, MicOff, Volume2 } from 'lucide-react';

type VoiceMode = 'browser' | 'whisper';

interface VoiceInputProps {
  onTranscript: (text: string) => void;
  placeholder?: string;
  mode?: VoiceMode;
}

export function VoiceInput({
  onTranscript,
  placeholder = 'Click mic to speak...',
  mode = 'browser',
}: VoiceInputProps) {
  const [isListening, setIsListening] = useState(false);
  const [transcript, setTranscript] = useState('');
  const [error, setError] = useState('');
  const [isTranscribing, setIsTranscribing] = useState(false);
  const [currentMode, setCurrentMode] = useState<VoiceMode>(mode);
  const [supportedModes, setSupportedModes] = useState<VoiceMode[]>([]);

  const recognitionRef = useRef<any>(null);
  const mediaRecorderRef = useRef<MediaRecorder | null>(null);
  const audioChunksRef = useRef<Blob[]>([]);

  useEffect(() => {
    const modes: VoiceMode[] = [];

    // Check browser speech support
    const SpeechRecognition =
      (window as any).webkitSpeechRecognition || (window as any).SpeechRecognition;
    if (SpeechRecognition) {
      modes.push('browser');
      recognitionRef.current = new SpeechRecognition();
      recognitionRef.current.continuous = false;
      recognitionRef.current.interimResults = true;
      recognitionRef.current.lang = 'en-US';

      recognitionRef.current.onstart = () => setIsListening(true);
      recognitionRef.current.onend = () => setIsListening(false);
      recognitionRef.current.onerror = (event: any) => {
        setError(`Browser error: ${event.error}`);
        setIsListening(false);
      };
      recognitionRef.current.onresult = (event: any) => {
        let interimTranscript = '';
        for (let i = event.resultIndex; i < event.results.length; i++) {
          const transcript = event.results[i][0].transcript;
          if (event.results[i].isFinal) {
            setTranscript((prev) => prev + transcript + ' ');
            onTranscript(transcript);
          } else {
            interimTranscript += transcript;
          }
        }
        if (interimTranscript) {
          setTranscript((prev) => prev.split(' ').slice(0, -1).join(' ') + ' ' + interimTranscript);
        }
      };
    }

    // Check Whisper support (requires recording capability)
    if (navigator.mediaDevices && navigator.mediaDevices.getUserMedia) {
      modes.push('whisper');
    }

    setSupportedModes(modes);

    // Set initial mode to first available
    if (modes.length > 0 && !modes.includes(mode)) {
      setCurrentMode(modes[0]);
    }
  }, [mode, onTranscript]);

  const startBrowserRecording = () => {
    if (!recognitionRef.current) {
      setError('Speech recognition not supported in this browser');
      return;
    }
    setError('');
    setTranscript('');
    recognitionRef.current.start();
  };

  const stopBrowserRecording = () => {
    if (recognitionRef.current) {
      recognitionRef.current.stop();
    }
  };

  const startWhisperRecording = async () => {
    try {
      setError('');
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const mediaRecorder = new MediaRecorder(stream);
      mediaRecorderRef.current = mediaRecorder;
      audioChunksRef.current = [];

      mediaRecorder.ondataavailable = (event) => {
        audioChunksRef.current.push(event.data);
      };

      mediaRecorder.onstop = async () => {
        const audioBlob = new Blob(audioChunksRef.current, { type: 'audio/wav' });
        await sendToWhisper(audioBlob);
        stream.getTracks().forEach((track) => track.stop());
      };

      mediaRecorder.start();
      setIsListening(true);
    } catch (err: any) {
      setError(`Microphone access denied: ${err.message}`);
    }
  };

  const stopWhisperRecording = () => {
    if (mediaRecorderRef.current && isListening) {
      mediaRecorderRef.current.stop();
      setIsListening(false);
    }
  };

  const sendToWhisper = async (audioBlob: Blob) => {
    setIsTranscribing(true);
    try {
      const formData = new FormData();
      formData.append('audio', audioBlob, 'audio.wav');

      const response = await fetch('/api/transcribe', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Transcription failed');
      }

      const data = await response.json();
      setTranscript(data.text);
      onTranscript(data.text);
    } catch (err: any) {
      setError(`Whisper error: ${err.message}`);
    } finally {
      setIsTranscribing(false);
    }
  };

  const toggleRecording = () => {
    if (currentMode === 'browser') {
      if (isListening) {
        stopBrowserRecording();
      } else {
        startBrowserRecording();
      }
    } else {
      if (isListening) {
        stopWhisperRecording();
      } else {
        startWhisperRecording();
      }
    }
  };

  return (
    <div className="w-full max-w-2xl mx-auto space-y-4">
      {/* Mode Selector */}
      {supportedModes.length > 1 && (
        <div className="flex gap-2">
          {supportedModes.map((m) => (
            <button
              key={m}
              onClick={() => setCurrentMode(m)}
              className={`px-3 py-1 rounded text-sm font-medium transition ${
                currentMode === m
                  ? 'bg-blue-600 text-white'
                  : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
              }`}
              disabled={isListening || isTranscribing}
            >
              {m === 'browser' ? 'Browser Speech' : 'Whisper'}
            </button>
          ))}
        </div>
      )}

      {/* Main Voice Input */}
      <div className="border-2 border-gray-300 rounded-lg p-6 space-y-4">
        <button
          onClick={toggleRecording}
          disabled={isTranscribing}
          className={`w-full py-4 rounded-lg font-bold text-white transition flex items-center justify-center gap-2 ${
            isListening
              ? 'bg-red-600 hover:bg-red-700'
              : 'bg-blue-600 hover:bg-blue-700'
          } ${isTranscribing ? 'opacity-50 cursor-not-allowed' : ''}`}
        >
          {isTranscribing ? (
            <>
              <Loader2 className="w-5 h-5 animate-spin" />
              Transcribing...
            </>
          ) : isListening ? (
            <>
              <MicOff className="w-5 h-5" />
              Stop Recording
            </>
          ) : (
            <>
              <Mic className="w-5 h-5" />
              Start Recording
            </>
          )}
        </button>

        {/* Transcript Display */}
        {transcript && (
          <div className="bg-blue-50 border border-blue-200 rounded p-4">
            <p className="text-sm text-gray-600 mb-1">Transcript:</p>
            <p className="text-lg text-gray-900">{transcript}</p>
          </div>
        )}

        {/* Error Display */}
        {error && (
          <div className="bg-red-50 border border-red-200 rounded p-4">
            <p className="text-sm text-red-700">{error}</p>
          </div>
        )}

        {/* Status Indicator */}
        {isListening && (
          <div className="flex items-center gap-2 text-sm text-orange-600">
            <Volume2 className="w-4 h-4 animate-pulse" />
            Listening... ({currentMode === 'browser' ? 'Browser' : 'Whisper'})
          </div>
        )}
      </div>

      {/* Mode Info */}
      <div className="text-xs text-gray-600 bg-gray-50 p-3 rounded">
        <p className="font-semibold mb-1">Current Mode: {currentMode === 'browser' ? 'Browser Speech API' : 'OpenAI Whisper'}</p>
        {currentMode === 'browser' && (
          <p>Works in Chrome/Edge/Safari. Less accurate but no API key needed.</p>
        )}
        {currentMode === 'whisper' && (
          <p>Requires OPENAI_API_KEY in .env.local. More accurate transcription.</p>
        )}
      </div>
    </div>
  );
}

'use client';

import React, { ButtonHTMLAttributes, ReactNode } from 'react';
import { useTheme } from '@/context/ThemeContext';

interface ButtonProps extends ButtonHTMLAttributes<HTMLButtonElement> {
  variant?: 'primary' | 'secondary' | 'outline' | 'ghost' | 'danger';
  size?: 'xs' | 'sm' | 'md' | 'lg' | 'xl';
  isLoading?: boolean;
  icon?: ReactNode;
  iconPosition?: 'left' | 'right';
  fullWidth?: boolean;
}

const sizeClasses = {
  xs: 'px-3 py-1.5 text-xs rounded-sm',
  sm: 'px-4 py-2 text-sm rounded-base',
  md: 'px-6 py-2.5 text-base rounded-md',
  lg: 'px-8 py-3 text-lg rounded-lg',
  xl: 'px-10 py-4 text-xl rounded-lg',
};

/**
 * Premium Button Component
 * Supports multiple variants and sizes with premium styling
 * Integrates with theme system for automatic color application
 */
export const Button: React.FC<ButtonProps> = ({
  variant = 'primary',
  size = 'md',
  isLoading = false,
  icon,
  iconPosition = 'left',
  fullWidth = false,
  disabled,
  className = '',
  children,
  ...props
}) => {
  const { currentTheme } = useTheme();

  const baseClasses =
    'inline-flex items-center justify-center font-medium transition-all duration-base font-accent';

  const variantClasses = {
    primary: 'bg-primary text-background hover:bg-primary-light hover:shadow-lg active:scale-95 disabled:opacity-50',
    secondary:
      'bg-surface border border-primary text-primary hover:bg-primary hover:text-background hover:shadow-lg active:scale-95',
    outline:
      'border-2 border-primary text-primary hover:bg-primary hover:text-background active:scale-95',
    ghost:
      'text-primary hover:bg-primary hover:text-background active:scale-95 hover:shadow-md',
    danger:
      'bg-error text-white hover:bg-red-600 hover:shadow-lg active:scale-95 disabled:opacity-50',
  };

  const widthClass = fullWidth ? 'w-full' : '';

  const finalClasses = `
    ${baseClasses}
    ${sizeClasses[size]}
    ${variantClasses[variant]}
    ${widthClass}
    ${disabled ? 'cursor-not-allowed opacity-50' : ''}
    ${className}
  `;

  return (
    <button
      className={finalClasses.trim()}
      disabled={disabled || isLoading}
      {...props}
    >
      {isLoading ? (
        <>
          <svg
            className="mr-2 h-4 w-4 animate-spin"
            xmlns="http://www.w3.org/2000/svg"
            fill="none"
            viewBox="0 0 24 24"
          >
            <circle
              className="opacity-25"
              cx="12"
              cy="12"
              r="10"
              stroke="currentColor"
              strokeWidth="4"
            />
            <path
              className="opacity-75"
              fill="currentColor"
              d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
            />
          </svg>
          Loading...
        </>
      ) : (
        <>
          {icon && iconPosition === 'left' && <span className="mr-2">{icon}</span>}
          {children}
          {icon && iconPosition === 'right' && <span className="ml-2">{icon}</span>}
        </>
      )}
    </button>
  );
};

export default Button;

