// Initialize configuration once
let configInstance: APIKeyConfig | null = null;

/**
 * Get validated configuration (lazy load)
 */
export function getConfig(): APIKeyConfig {
  if (!configInstance) {
    configInstance = validateConfig();
  }
  return configInstance;
}

/**
 * Force reload configuration (useful for testing)
 */
export function reloadConfig(): APIKeyConfig {
  configInstance = null;
  return getConfig();
}

/**
 * Check if a specific API is configured
 */
export function isAPIConfigured(apiName: keyof APIKeyConfig): boolean {
  const config = getConfig();
  const apiConfig = config[apiName];
  
  if (typeof apiConfig === 'object' && apiConfig !== null) {
    const values = Object.values(apiConfig);
    return values.some(v => {
      if (typeof v === 'object') {
        return Object.values(v).some(val => val && String(val).length > 0);
      }
      return v && String(v).length > 0;
    });
  }
  
  return false;
}

/**
 * Log configuration status
 */
export function logConfigStatus(): void {
  const config = getConfig();
  
  console.log('\n Configuration Status:');
  console.log('========================\n');
  
  const apis: (keyof APIKeyConfig)[] = [
    'google',
    'openai',
    'stripe',
    'firebase',
    'communications',
    'messaging',
    'security',
    'monitoring',
  ];
  
  for (const api of apis) {
    const status = isAPIConfigured(api) ? '' : '';
    console.log(`${status} ${api.toUpperCase()}`);
  }
  
  console.log('\n========================\n');
}

// Export a singleton instance getter
export const Config = {
  get: getConfig,
  reload: reloadConfig,
  isConfigured: isAPIConfigured,
  logStatus: logConfigStatus,
};

export default getConfig;

// Google Cloud Identity Platform (GCIP) Configuration
// This file manages auth providers and user sessions

import { 
  signInWithPopup, 
  signOut, 
  onAuthStateChanged, 
  GoogleAuthProvider,
  FacebookAuthProvider,
  TwitterAuthProvider,
  GithubAuthProvider,
  User,
  AuthProvider,
  signInWithEmailAndPassword,
  createUserWithEmailAndPassword,
  RecaptchaVerifier,
  multiFactor,
  PhoneMultiFactorGenerator,
  PhoneAuthProvider,
  PhoneAuthCredential
} from 'firebase/auth';
import { auth } from './firebase';

if (!auth) {
  throw new Error('Firebase auth is not initialized');
}

// Initialize all OAuth providers
const googleProvider = new GoogleAuthProvider();
const facebookProvider = new FacebookAuthProvider();
const twitterProvider = new TwitterAuthProvider();
const githubProvider = new GithubAuthProvider();

// Add scopes for enhanced data access
googleProvider.addScope('profile');
googleProvider.addScope('email');
facebookProvider.addScope('email');
twitterProvider.addScope('tweet.read');
githubProvider.addScope('user:email');

// Provider map for dynamic sign-in
export const providers: Record<string, AuthProvider> = {
  google: googleProvider,
  facebook: facebookProvider,
  twitter: twitterProvider,
  github: githubProvider,
};

// Sign in with OAuth provider
export async function signInWithProvider(providerName: string): Promise<User> {
  const provider = providers[providerName];
  if (!provider) throw new Error(`Unknown provider: ${providerName}`);
  if (!auth) throw new Error('Firebase auth is not initialized');
  
  const result = await signInWithPopup(auth, provider);
  return result.user;
}

// Email/Password sign up
export async function signUpWithEmail(email: string, password: string): Promise<User> {
  if (!auth) throw new Error('Firebase auth is not initialized');
  const result = await createUserWithEmailAndPassword(auth, email, password);
  return result.user;
}

// Email/Password sign in
export async function signInWithEmail(email: string, password: string): Promise<User> {
  if (!auth) throw new Error('Firebase auth is not initialized');
  const result = await signInWithEmailAndPassword(auth, email, password);
  return result.user;
}

// Sign out
export async function signOutUser(): Promise<void> {
  if (!auth) throw new Error('Firebase auth is not initialized');
  await signOut(auth);
}

// Monitor auth state changes
export function onAuthChange(callback: (user: User | null) => void): () => void {
  if (!auth) throw new Error('Firebase auth is not initialized');
  return onAuthStateChanged(auth, callback);
}

// Get current user
export function getCurrentUser(): User | null {
  if (!auth) throw new Error('Firebase auth is not initialized');
  return auth.currentUser;
}

// Setup MFA (Multi-Factor Authentication)
export async function enableMFAPhone(phoneNumber: string): Promise<string> {
  if (!auth) throw new Error('Firebase auth is not initialized');
  const user = getCurrentUser();
  if (!user) throw new Error('No authenticated user');

  const recaptchaVerifier = new RecaptchaVerifier(auth, 'recaptcha-container', {
    size: 'invisible',
  });

  const phoneAuthProvider = new PhoneAuthProvider(auth);
  const verificationId = await phoneAuthProvider.verifyPhoneNumber(
    phoneNumber,
    recaptchaVerifier
  );

  return verificationId;
}

// Complete MFA verification
export async function completeMFAVerification(verificationId: string, code: string): Promise<void> {
  const user = getCurrentUser();
  if (!user) throw new Error('No authenticated user');
  if (!auth) throw new Error('Firebase auth is not initialized');

  // PhoneAuthCredential.credential is deprecated, use proper MFA enrollment
  // This function requires server-side verification for security
  try {
    // Create a temporary credential object for assertion
    const credential = { verificationId, verificationCode: code } as any;
    const multiFactorAssertion = PhoneMultiFactorGenerator.assertion(credential as any);
    
    await multiFactor(user).enroll(multiFactorAssertion, 'My Phone');
  } catch (error) {
    // In production, phone MFA verification should be handled server-side
    throw new Error('MFA enrollment requires server-side verification');
  }
}

// Link additional provider to existing account
export async function linkProviderToAccount(providerName: string): Promise<User> {
  const user = getCurrentUser();
  if (!user) throw new Error('No authenticated user');
  if (!auth) throw new Error('Firebase auth is not initialized');

  const provider = providers[providerName];
  if (!provider) throw new Error(`Unknown provider: ${providerName}`);

  const result = await signInWithPopup(auth, provider);
  // Note: In real implementation, you'd use linkWithCredential
  return result.user;
}

// Get user's auth providers
export function getLinkedProviders(): string[] {
  const user = getCurrentUser();
  if (!user) return [];
  
  return user.providerData.map(p => p.providerId);
}

// Delete account
export async function deleteUserAccount(): Promise<void> {
  const user = getCurrentUser();
  if (!user) throw new Error('No authenticated user');
  
  await user.delete();
}

// Check if user has MFA enabled
export function hasMFAEnabled(): boolean {
  const user = getCurrentUser();
  if (!user) return false;
  
  return multiFactor(user).enrolledFactors.length > 0;
}

/**
 *  NATS Consumer System
 * Processes tasks from NATS message queue
 * Monitors, logs, and handles errors
 */

import { connect, NatsConnection, Subscription, consumerOpts } from 'nats';
import { getConfig } from './config';
import { captureException, captureMessage } from './sentry';

export interface ConsumerMetrics {
  messagesProcessed: number;
  messagesFailed: number;
  averageProcessingTime: number;
  lastProcessed: Date | null;
  lastError: Error | null;
  uptime: Date;
}

class NATSConsumer {
  private static instance: NATSConsumer;
  private connection: NatsConnection | null = null;
  private subscription: Subscription | null = null;
  private metrics: ConsumerMetrics = {
    messagesProcessed: 0,
    messagesFailed: 0,
    averageProcessingTime: 0,
    lastProcessed: null,
    lastError: null,
    uptime: new Date(),
  };
  private isRunning = false;
  private processingTimes: number[] = [];

  /**
   * Singleton pattern
   */
  static getInstance(): NATSConsumer {
    if (!NATSConsumer.instance) {
      NATSConsumer.instance = new NATSConsumer();
    }
    return NATSConsumer.instance;
  }

  /**
   * Initialize NATS connection
   */
  async initialize(): Promise<void> {
    try {
      const config = getConfig();

      if (!config.messaging.nats.url) {
        console.warn('  NATS not configured, skipping consumer initialization');
        return;
      }

      this.connection = await connect({
        servers: config.messaging.nats.url,
        user: config.messaging.nats.user,
        pass: config.messaging.nats.password,
        timeout: 10000,
      });

      console.log(' NATS connected');

      // Create jetstream context
      const nc = this.connection;
      const jsm = await nc.jetstreamManager();

      // Ensure stream exists
      try {
        await jsm.streams.info('LITLABS');
      } catch {
        // Create stream if it doesn't exist
        await jsm.streams.add({
          name: 'LITLABS',
          subjects: ['tasks.>', 'events.>'],
          discard: 'old',
          max_age: 24 * 60 * 60 * 1000000000, // 24 hours in nanoseconds
          retention: 'limits',
        });
        console.log(' Created NATS stream: LITLABS');
      }

      captureMessage('NATS consumer initialized', 'info');
    } catch (error) {
      console.error('NATS initialization failed:', error);
      captureException(error, 'nats_init_error');
      throw error;
    }
  }

  /**
   * Start consuming tasks
   */
  async startConsuming(): Promise<void> {
    if (this.isRunning) {
      console.warn('NATS consumer already running');
      return;
    }

    if (!this.connection) {
      await this.initialize();
    }

    try {
      const nc = this.connection;
      if (!nc) throw new Error('NATS connection not established');

      const js = nc.jetstream();

      // Create consumer
      const opts = consumerOpts();
      opts.durable('litlabs-task-consumer');
      opts.manualAck();
      opts.ackWait(30 * 1000); // 30 seconds
      opts.maxDeliver(3);

      // Subscribe to tasks
      this.subscription = await js.subscribe('tasks.>', opts);

      this.isRunning = true;
      console.log(' NATS consumer started');

      // Process messages
      (async () => {
        for await (const msg of this.subscription!) {
          const startTime = Date.now();
          try {
            await this.processMessage(msg);
            msg.ack();

            const duration = Date.now() - startTime;
            this.recordMetrics(duration, true);
            this.metrics.lastProcessed = new Date();
          } catch (error) {
            console.error('Message processing error:', error);
            this.recordMetrics(Date.now() - startTime, false, error as Error);

            // Requeue on error
            msg.nak();
          }
        }
      })().catch(err => {
        console.error('NATS subscription error:', err);
        captureException(err, 'nats_subscription_error');
      });

      captureMessage('NATS consumer started consuming', 'info');
    } catch (error) {
      console.error('Failed to start consuming:', error);
      captureException(error, 'nats_start_consuming_error');
      throw error;
    }
  }

  /**
   * Process individual message
   */
  private async processMessage(msg: any): Promise<void> {
    try {
      const data = JSON.parse(new TextDecoder().decode(msg.data));

      // Route message to appropriate handler
      if (msg.subject.startsWith('tasks.')) {
        await this.handleTaskMessage(data);
      } else if (msg.subject.startsWith('events.')) {
        await this.handleEventMessage(data);
      }
    } catch (error) {
      console.error('Error processing message:', error);
      throw error;
    }
  }

  /**
   * Handle task message
   */
  private async handleTaskMessage(data: any): Promise<void> {
    const { taskId, type, userId, payload } = data;

    console.log(` Processing task ${taskId} (${type})`);

    // Dispatch to appropriate handler
    switch (type) {
      case 'ai_generation':
        await this.handleAIGeneration(taskId, userId, payload);
        break;
      case 'email_sequence':
        await this.handleEmailSequence(taskId, userId, payload);
        break;
      case 'automation':
        await this.handleAutomation(taskId, userId, payload);
        break;
      default:
        console.warn(`Unknown task type: ${type}`);
    }
  }

  /**
   * Handle event message
   */
  private async handleEventMessage(data: any): Promise<void> {
    const { eventType, userId, eventData } = data;
    console.log(` Processing event ${eventType} for user ${userId}`);
    // Handle events (webhooks, notifications, etc.)
  }

  /**
   * Handle AI generation task
   */
  private async handleAIGeneration(taskId: string, userId: string, payload: any): Promise<void> {
    // Actual implementation would call AI generation service
    console.log(`Generating AI content for task ${taskId}`);
    // await generateContent(payload);
  }

  /**
   * Handle email sequence task
   */
  private async handleEmailSequence(taskId: string, userId: string, payload: any): Promise<void> {
    console.log(`Sending email sequence for task ${taskId}`);
    // await sendEmailSequence(userId, payload);
  }

  /**
   * Handle automation task
   */
  private async handleAutomation(taskId: string, userId: string, payload: any): Promise<void> {
    console.log(`Executing automation ${taskId}`);
    // await executeAutomation(userId, payload);
  }

  /**
   * Record processing metrics
   */
  private recordMetrics(duration: number, success: boolean, error?: Error): void {
    if (success) {
      this.metrics.messagesProcessed++;
      this.processingTimes.push(duration);

      // Keep only last 100 processing times for average
      if (this.processingTimes.length > 100) {
        this.processingTimes.shift();
      }

      this.metrics.averageProcessingTime =
        this.processingTimes.reduce((a, b) => a + b, 0) / this.processingTimes.length;
    } else {
      this.metrics.messagesFailed++;
      if (error) {
        this.metrics.lastError = error;
      }
    }
  }

  /**
   * Get metrics
   */
  getMetrics(): ConsumerMetrics {
    return { ...this.metrics };
  }

  /**
   * Log metrics
   */
  logMetrics(): void {
    console.log('\n NATS CONSUMER METRICS');
    console.log('========================\n');
    console.log(` Messages Processed: ${this.metrics.messagesProcessed}`);
    console.log(` Messages Failed: ${this.metrics.messagesFailed}`);
    console.log(`  Avg Processing Time: ${Math.round(this.metrics.averageProcessingTime)}ms`);
    console.log(` Last Processed: ${this.metrics.lastProcessed || 'Never'}`);
    console.log(` Uptime: ${Math.round((Date.now() - this.metrics.uptime.getTime()) / 1000)}s`);
    if (this.metrics.lastError) {
      console.log(`  Last Error: ${this.metrics.lastError.message}`);
    }
    console.log('\n========================\n');
  }

  /**
   * Publish task to NATS
   */
  async publishTask(taskId: string, type: string, userId: string, payload: any): Promise<void> {
    if (!this.connection) {
      throw new Error('NATS not initialized');
    }

    const js = this.connection.jetstream();
    const data = JSON.stringify({ taskId, type, userId, payload });

    await js.publish(`tasks.${type}`, new TextEncoder().encode(data));
    console.log(` Published task to NATS: ${taskId}`);
  }

  /**
   * Stop consuming
   */
  async stop(): Promise<void> {
    if (this.subscription) {
      await this.subscription.unsubscribe();
      this.subscription = null;
    }

    if (this.connection) {
      await this.connection.close();
      this.connection = null;
    }

    this.isRunning = false;
    console.log(' NATS consumer stopped');
  }

  /**
   * Health check
   */
  isHealthy(): boolean {
    return this.isRunning && this.connection !== null;
  }
}

// Export singleton
export const Consumer = NATSConsumer.getInstance();

/**
 * Initialize consumer
 */
export async function initializeNATSConsumer(): Promise<void> {
  await Consumer.initialize();
  await Consumer.startConsuming();
}

export default Consumer;

/**
 *  Server Initialization System
 * Handles all startup tasks, API validation, and service initialization
 */

import { getConfig, Config } from './config';
import { initializeApp as initializeFirebase } from 'firebase/app';
import { GoogleGenerativeAI } from '@google/generative-ai';
import OpenAI from 'openai';
import { captureMessage, captureException } from './sentry';

/**
 * Service initialization status
 */
export interface InitializationStatus {
  timestamp: Date;
  firebase: { initialized: boolean; error?: string };
  googleAI: { initialized: boolean; error?: string };
  openAI: { initialized: boolean; error?: string };
  stripe: { initialized: boolean; error?: string };
  nats: { initialized: boolean; error?: string };
  redis: { initialized: boolean; error?: string };
  email: { initialized: boolean; error?: string };
  allHealthy: boolean;
}

class ServerInitializer {
  private static instance: ServerInitializer;
  private initialized = false;
  private initializationStatus: InitializationStatus = {
    timestamp: new Date(),
    firebase: { initialized: false },
    googleAI: { initialized: false },
    openAI: { initialized: false },
    stripe: { initialized: false },
    nats: { initialized: false },
    redis: { initialized: false },
    email: { initialized: false },
    allHealthy: false,
  };

  private googleAI: GoogleGenerativeAI | null = null;
  private openAIClient: OpenAI | null = null;

  /**
   * Singleton pattern
   */
  static getInstance(): ServerInitializer {
    if (!ServerInitializer.instance) {
      ServerInitializer.instance = new ServerInitializer();
    }
    return ServerInitializer.instance;
  }

  /**
   * Initialize all services on startup
   */
  async initialize(): Promise<InitializationStatus> {
    if (this.initialized) {
      console.log(' Server already initialized');
      return this.initializationStatus;
    }

    console.log('\n Starting LitLabs Server Initialization...\n');
    
    const config = getConfig();
    
    // Run all initialization tasks in parallel
    await Promise.all([
      this.initializeFirebase(config),
      this.initializeGoogleAI(config),
      this.initializeOpenAI(config),
      this.initializeStripe(config),
      this.initializeNATS(config),
      this.initializeRedis(config),
      this.initializeEmail(config),
    ]);

    // Determine overall health
    const services = Object.entries(this.initializationStatus)
      .filter(([key]) => key !== 'timestamp' && key !== 'allHealthy')
      .map(([_, status]) => status as any);

    const criticalServices = ['firebase', 'googleAI', 'stripe'];
    const allCriticalHealthy = criticalServices.every(
      service => this.initializationStatus[service as keyof Omit<InitializationStatus, 'timestamp' | 'allHealthy'>].initialized
    );

    this.initializationStatus.allHealthy = allCriticalServices && allCriticalHealthy;
    this.initializationStatus.timestamp = new Date();

    this.logInitializationStatus();

    this.initialized = true;
    return this.initializationStatus;
  }

  /**
   * Initialize Firebase
   */
  private async initializeFirebase(config: any): Promise<void> {
    try {
      // Firebase initialization is handled by the client/admin libraries
      // Just verify configuration is present
      if (!config.firebase.projectId || !config.firebase.apiKey) {
        throw new Error('Firebase configuration incomplete');
      }

      this.initializationStatus.firebase.initialized = true;
      console.log(' Firebase initialized');
    } catch (error) {
      const msg = error instanceof Error ? error.message : String(error);
      this.initializationStatus.firebase.error = msg;
      console.error(' Firebase initialization failed:', msg);
      captureException(error, 'firebase_init_error');
    }
  }

  /**
   * Initialize Google Generative AI (Gemini)
   */
  private async initializeGoogleAI(config: any): Promise<void> {
    try {
      if (!config.google.generativeAI) {
        throw new Error('GOOGLE_GENERATIVE_AI_API_KEY not configured');
      }

      this.googleAI = new GoogleGenerativeAI(config.google.generativeAI);

      // Test the API connection
      const model = this.googleAI.getGenerativeModel({ model: 'gemini-1.5-flash' });
      await model.generateContent('Test');

      this.initializationStatus.googleAI.initialized = true;
      console.log(' Google Generative AI (Gemini) initialized');
    } catch (error) {
      const msg = error instanceof Error ? error.message : String(error);
      this.initializationStatus.googleAI.error = msg;
      console.error(' Google AI initialization failed:', msg);
      captureException(error, 'google_ai_init_error');
    }
  }

  /**
   * Initialize OpenAI (ChatGPT, GPT-4)
   */
  private async initializeOpenAI(config: any): Promise<void> {
    try {
      if (!config.openai.apiKey) {
        console.warn('  OpenAI not configured (optional)');
        return;
      }

      this.openAIClient = new OpenAI({
        apiKey: config.openai.apiKey,
        organization: config.openai.orgId,
      });

      // Test the API connection
      await this.openAIClient.models.list();

      this.initializationStatus.openAI.initialized = true;
      console.log(' OpenAI (ChatGPT/GPT-4) initialized');
    } catch (error) {
      const msg = error instanceof Error ? error.message : String(error);
      this.initializationStatus.openAI.error = msg;
      console.warn('  OpenAI initialization failed (optional):', msg);
    }
  }

  /**
   * Initialize Stripe
   */
  private async initializeStripe(config: any): Promise<void> {
    try {
      if (!config.stripe.secret || !config.stripe.publishable) {
        throw new Error('Stripe keys not configured');
      }

      // Stripe initialization is handled by getStripe() in lib/stripe.ts
      // Just verify configuration
      if (!config.stripe.prices.pro || !config.stripe.prices.starter) {
        console.warn('  Some Stripe price IDs not configured');
      }

      this.initializationStatus.stripe.initialized = true;
      console.log(' Stripe initialized');
    } catch (error) {
      const msg = error instanceof Error ? error.message : String(error);
      this.initializationStatus.stripe.error = msg;
      console.error(' Stripe initialization failed:', msg);
      captureException(error, 'stripe_init_error');
    }
  }

  /**
   * Initialize NATS (Message Queue)
   */
  private async initializeNATS(config: any): Promise<void> {
    try {
      if (!config.messaging.nats.url) {
        console.warn('  NATS not configured (optional)');
        return;
      }

      // NATS connection is lazy-initialized when needed
      console.log(' NATS configured (lazy initialization)');
      this.initializationStatus.nats.initialized = true;
    } catch (error) {
      const msg = error instanceof Error ? error.message : String(error);
      this.initializationStatus.nats.error = msg;
      console.warn('  NATS configuration incomplete:', msg);
    }
  }

  /**
   * Initialize Redis (Cache/Rate Limiting)
   */
  private async initializeRedis(config: any): Promise<void> {
    try {
      if (!config.messaging.redis.url) {
        console.warn('  Redis not configured (optional)');
        return;
      }

      // Redis connection is lazy-initialized when needed
      console.log(' Redis configured (lazy initialization)');
      this.initializationStatus.redis.initialized = true;
    } catch (error) {
      const msg = error instanceof Error ? error.message : String(error);
      this.initializationStatus.redis.error = msg;
      console.warn('  Redis configuration incomplete:', msg);
    }
  }

  /**
   * Initialize Email Service (Resend)
   */
  private async initializeEmail(config: any): Promise<void> {
    try {
      if (!config.communications.resend.apiKey) {
        console.warn('  Email (Resend) not configured (optional)');
        return;
      }

      this.initializationStatus.email.initialized = true;
      console.log(' Email service (Resend) configured');
    } catch (error) {
      const msg = error instanceof Error ? error.message : String(error);
      this.initializationStatus.email.error = msg;
      console.warn('  Email service initialization failed:', msg);
    }
  }

  /**
   * Log initialization status
   */
  private logInitializationStatus(): void {
    console.log('\n INITIALIZATION STATUS REPORT');
    console.log('================================\n');

    const status = this.initializationStatus;
    const formatStatus = (s: any) => s.initialized ? '' : ` ${s.error || 'Not configured'}`;

    console.log(`Firebase:    ${formatStatus(status.firebase)}`);
    console.log(`Google AI:   ${formatStatus(status.googleAI)}`);
    console.log(`OpenAI:      ${formatStatus(status.openAI)}`);
    console.log(`Stripe:      ${formatStatus(status.stripe)}`);
    console.log(`NATS:        ${formatStatus(status.nats)}`);
    console.log(`Redis:       ${formatStatus(status.redis)}`);
    console.log(`Email:       ${formatStatus(status.email)}`);

    console.log('\n' + (status.allHealthy 
      ? ' ALL CRITICAL SERVICES INITIALIZED' 
      : ' SOME CRITICAL SERVICES FAILED'));
    console.log('================================\n');

    if (status.allHealthy) {
      captureMessage('Server initialization successful', 'info');
    }
  }

  /**
   * Get Google AI instance
   */
  getGoogleAI(): GoogleGenerativeAI | null {
    return this.googleAI;
  }

  /**
   * Get OpenAI instance
   */
  getOpenAI(): OpenAI | null {
    return this.openAIClient;
  }

  /**
   * Get initialization status
   */
  getStatus(): InitializationStatus {
    return this.initializationStatus;
  }

  /**
   * Health check endpoint
   */
  async healthCheck(): Promise<InitializationStatus> {
    return this.initializationStatus;
  }
}

