/**
 * Monetization Dashboard API
 * Complete view of earnings, subscriptions, affiliates, add-ons
 */

import { NextRequest, NextResponse } from 'next/server';
import { getUserFromRequest } from '@/lib/firebase-server';
import { captureError } from '@/lib/sentry';
import {
  getUserSubscription,
  getTierDetails,
  getTeamMembers,
} from '@/lib/subscription-manager';
import {
  getWhiteLabelConfig,
} from '@/lib/white-label';
import {
  getRevenueMetricsRange,
} from '@/lib/advanced-analytics';

export const runtime = 'nodejs';
export const dynamic = 'force-dynamic';

/**
 * GET /api/monetization/dashboard
 * Complete monetization overview
 */
export async function GET(request: NextRequest) {
  try {
    const user = await getUserFromRequest(request);
    if (!user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    // Get subscription info
    const subscription = await getUserSubscription(user.uid);
    const tier = subscription ? getTierDetails(subscription.tier) : getTierDetails('free');

    // Get team info
    const teamMembers = await getTeamMembers(user.uid);
    const activeMembers = teamMembers.filter((m: any) => m.isActive).length;

    // Get white-label config
    const whiteLabelConfig = await getWhiteLabelConfig(user.uid);

    // Initialize affiliate profile as null
    const affiliateProfile = null;

    // Get revenue metrics for current month
    const today = new Date();
    const currentMonth = today.toISOString().slice(0, 7);
    const lastMonth = new Date(today.getFullYear(), today.getMonth() - 1, 1)
      .toISOString()
      .slice(0, 7);

    const revenueData = await getRevenueMetricsRange(
      user.uid,
      lastMonth,
      currentMonth
    );
    const currentMonthRevenue = revenueData.find(r => r.month === currentMonth);
    const lastMonthRevenue = revenueData.find(r => r.month === lastMonth);

    const mrrGrowth =
      lastMonthRevenue && currentMonthRevenue
        ? (
            ((currentMonthRevenue.mrr - lastMonthRevenue.mrr) /
              Math.max(1, lastMonthRevenue.mrr)) *
            100
          ).toFixed(1)
        : '0';

    return NextResponse.json({
      success: true,
      dashboard: {
        subscriptionStatus: {
          tier: tier.name,
          description: tier.description,
          price: tier.price > 0 ? `$${tier.price}/month` : 'Free',
          users: `${activeMembers}/${tier.users}`,
          storage: `${subscription?.storageUsed || 0}GB / ${tier.storage}GB`,
          nextBillingDate: subscription?.nextBillingDate?.toISOString(),
          autoRenew: subscription?.autoRenew ?? true,
          status: subscription?.status || 'inactive',
        },
        team: {
          totalMembers: activeMembers,
          maxMembers: tier.users,
          members: teamMembers
            .filter(m => m.isActive)
            .map(m => ({
              name: m.name || m.email,
              email: m.email,
              role: m.role,
              joinedAt: m.joinedAt.toISOString(),
            })),
        },
        affiliate: {
          status: 'inactive',
          message: 'Not enrolled in affiliate program',
        },
        whiteLabelFeatures: whiteLabelConfig
          ? {
              status: 'active',
              companyName: whiteLabelConfig.companyName,
              customDomain: whiteLabelConfig.customDomain,
              primaryColor: whiteLabelConfig.primaryColor,
              features: Object.entries(whiteLabelConfig.features)
                .filter(([, enabled]) => enabled)
                .map(([feature]) => feature),
            }
          : {
              status: 'inactive',
              message: 'White-label features not configured',
            },
        revenue: {
          currentMonthMRR: `$${(currentMonthRevenue?.mrr || 0).toFixed(2)}`,
          lastMonthMRR: `$${(lastMonthRevenue?.mrr || 0).toFixed(2)}`,
          growth: `${mrrGrowth}%`,
          totalRevenue: `$${(currentMonthRevenue?.totalRevenue || 0).toFixed(2)}`,
          subscriptionRevenue: `$${(
            currentMonthRevenue?.subscriptionRevenue || 0
          ).toFixed(2)}`,
          affiliateRevenue: `$${(currentMonthRevenue?.affiliateRevenue || 0).toFixed(2)}`,
          addonRevenue: `$${(currentMonthRevenue?.addonRevenue || 0).toFixed(2)}`,
          churnRate: `${(currentMonthRevenue?.churnRate || 0).toFixed(1)}%`,
        },
        features: {
          customization: {
            whiteLabel: tier.features.includes('Custom integrations'),
            customDomain: whiteLabelConfig?.customDomain !== undefined,
            teamCollaboration: tier.users > 1,
            advancedReporting: tier.features.includes('Advanced reporting'),
            apiAccess: tier.features.includes('API access'),
          },
          limits: {
            aiGenerations: tier.limits.aiGenerations === -1 ? 'Unlimited' : tier.limits.aiGenerations,
            dmReplies: tier.limits.dmReplies === -1 ? 'Unlimited' : tier.limits.dmReplies,
            moneyPlays: tier.limits.moneyPlays === -1 ? 'Unlimited' : tier.limits.moneyPlays,
            imageGenerations: tier.limits.imageGenerations === -1 ? 'Unlimited' : tier.limits.imageGenerations,
            videoGenerations: tier.limits.videoGenerations === -1 ? 'Unlimited' : tier.limits.videoGenerations,
          },
        },
      },
    });
  } catch (error) {
    captureError(error, { context: 'api/monetization/dashboard' });
    return NextResponse.json(
      { error: 'Failed to fetch monetization dashboard' },
      { status: 500 }
    );
  }
}

/**
 * POST /api/monetization/upgrade
 * Upgrade subscription tier
 */
export async function POST(request: NextRequest) {
  try {
    const user = await getUserFromRequest(request);
    if (!user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    const body = await request.json();
    const { targetTier } = body;

    if (!targetTier) {
      return NextResponse.json({ error: 'Target tier is required' }, { status: 400 });
    }

    // TODO: Create Stripe checkout session for upgrade
    // This would integrate with the Stripe billing module

    return NextResponse.json({
      success: true,
      message: `Upgrade to ${targetTier} initiated`,
      checkoutUrl: `/checkout?tier=${targetTier}`,
    });
  } catch (error) {
    captureError(error, { context: 'api/monetization/upgrade' });
    return NextResponse.json(
      { error: 'Failed to initiate upgrade' },
      { status: 500 }
    );
  }
}

import { NextRequest, NextResponse } from 'next/server';

export const runtime = 'nodejs';
export const dynamic = 'force-dynamic';

export async function GET(request: NextRequest) {
  return NextResponse.json({ message: 'Premium API' });
}

export async function POST(request: NextRequest) {
  return NextResponse.json({ error: 'Not implemented' }, { status: 501 });
}

'use client';

import React, { useState, useEffect } from 'react';
import { Play, Search, Filter, Clock, Star, Download } from 'lucide-react';
import DashboardLayout from '@/components/DashboardLayout';

interface MediaItem {
  id: string;
  title: string;
  poster: string;
  type: 'movie' | 'series' | 'music' | 'photo';
  progress: number; // 0-100
  duration: number; // in minutes
  source: 'youtube' | 'plex' | 'jellyfin' | 'drive' | 'tmdb';
  rating: number;
}



export default function MediaHubPage() {
  const [searchTerm, setSearchTerm] = useState('');
  const [filterType, setFilterType] = useState<'all' | 'movie' | 'series' | 'music' | 'photo'>('all');
  const [isMounted, setIsMounted] = useState(false);
  const [media, setMedia] = useState<MediaItem[]>([]);

  useEffect(() => {
    setIsMounted(true);
    // TODO: Load media from API or Firebase
  }, []);

  const filteredMedia = media.filter((item) => {
    const matchesSearch = item.title.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesType = filterType === 'all' || item.type === filterType;
    return matchesSearch && matchesType;
  });

  if (!isMounted) return null;

  return (
    <DashboardLayout>
      <div className="min-h-screen bg-gradient-to-br from-black via-gray-900 to-black">
        {/* Header */}
        <div className="border-b border-cyan-500/20 bg-black/80 backdrop-blur-md sticky top-0 z-40">
          <div className="max-w-7xl mx-auto px-6 py-6">
            <h1 className="text-3xl font-bold bg-gradient-to-r from-cyan-400 to-purple-400 bg-clip-text text-transparent mb-6">
               MediaHub
            </h1>

            {/* Search & Filters */}
            <div className="space-y-4">
              <div className="flex gap-3">
                <div className="flex-1 relative">
                  <Search className="absolute left-3 top-3 text-gray-400 pointer-events-none" size={20} />
                  <input
                    type="text"
                    placeholder="Search movies, series, music..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="w-full bg-black/50 border border-cyan-500/30 rounded-lg pl-10 pr-4 py-2 text-cyan-300 placeholder-gray-500 focus:outline-none focus:border-cyan-500"
                  />
                </div>
                <button className="px-4 py-2 bg-cyan-500/20 hover:bg-cyan-500/30 border border-cyan-500/30 rounded-lg transition-colors flex items-center gap-2 text-cyan-300">
                  <Filter size={20} />
                  Filters
                </button>
              </div>

              {/* Type Filter */}
              <div className="flex gap-2 overflow-x-auto pb-2">
                {(['all', 'movie', 'series', 'music', 'photo'] as const).map((type) => (
                  <button
                    key={type}
                    onClick={() => setFilterType(type)}
                    className={`px-4 py-2 rounded-lg transition-colors whitespace-nowrap text-sm font-semibold ${
                      filterType === type
                        ? 'bg-cyan-500/30 border border-cyan-500 text-cyan-300'
                        : 'bg-black/50 border border-cyan-500/20 text-gray-300 hover:border-cyan-500/40'
                    }`}
                  >
                    {type.charAt(0).toUpperCase() + type.slice(1)}
                  </button>
                ))}
              </div>
            </div>
          </div>
        </div>

        {/* Continue Watching */}
        <div className="max-w-7xl mx-auto px-6 py-8">
          <div className="mb-12">
            <h2 className="text-2xl font-bold text-cyan-300 mb-6 flex items-center gap-2">
              <Clock size={24} />
              Continue Watching
            </h2>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {filteredMedia
                .filter((m) => m.progress > 0 && m.progress < 100)
                .map((item) => (
                  <MediaCard key={item.id} item={item} />
                ))}
            </div>
          </div>

          {/* Trending */}
          <div className="mb-12">
            <h2 className="text-2xl font-bold text-purple-300 mb-6 flex items-center gap-2">
              <Star size={24} />
              Trending Now
            </h2>
            <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-5 gap-4">
              {filteredMedia.map((item) => (
                <div
                  key={item.id}
                  className="group cursor-pointer relative rounded-lg overflow-hidden"
                >
                  <img
                    src={item.poster}
                    alt={item.title}
                    className="w-full h-48 object-cover group-hover:scale-110 transition-transform duration-300"
                  />
                  <div className="absolute inset-0 bg-black/60 opacity-0 group-hover:opacity-100 transition-opacity flex items-center justify-center">
                    <Play className="text-white fill-white" size={40} />
                  </div>
                  <div className="absolute bottom-0 left-0 right-0 bg-gradient-to-t from-black to-transparent p-2">
                    <p className="text-xs font-semibold text-white truncate">{item.title}</p>
                    <p className="text-xs text-gray-300">{item.source}</p>
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Your Library */}
          <div>
            <h2 className="text-2xl font-bold text-green-300 mb-6 flex items-center gap-2">
              <Download size={24} />
              Your Library
            </h2>
            <div className="grid grid-cols-1 gap-4">
              {filteredMedia.map((item) => (
                <div
                  key={item.id}
                  className="flex gap-4 p-4 bg-black/50 border border-cyan-500/10 rounded-lg hover:border-cyan-500/30 transition-colors group cursor-pointer"
                >
                  <img
                    src={item.poster}
                    alt={item.title}
                    className="w-32 h-48 object-cover rounded group-hover:scale-105 transition-transform"
                  />
                  <div className="flex-1 flex flex-col justify-between py-2">
                    <div>
                      <h3 className="text-lg font-semibold text-cyan-300">{item.title}</h3>
                      <p className="text-sm text-gray-400 mt-1">
                        {item.type.charAt(0).toUpperCase() + item.type.slice(1)}  {item.duration} min
                      </p>
                      <div className="flex items-center gap-3 mt-3">
                        <div className="flex-1 max-w-xs bg-gray-700 h-1 rounded-full overflow-hidden">
                          { }
                          <div
                            className="bg-gradient-to-r from-cyan-500 to-purple-500 h-full"
                            style={{ width: `${item.progress}%` }}
                          />
                        </div>
                        <span className="text-xs text-gray-400">{item.progress}%</span>
                      </div>
                      <p className="text-sm text-yellow-400 mt-2"> {item.rating}/10</p>
                    </div>
                    <div className="flex gap-2">
                      <button className="px-4 py-2 bg-cyan-500/20 hover:bg-cyan-500/30 border border-cyan-500/30 rounded transition-colors text-sm font-semibold text-cyan-300 flex items-center gap-2">
                        <Play size={16} />
                        Play
                      </button>
                      <button className="px-4 py-2 bg-gray-700/20 hover:bg-gray-700/30 border border-gray-600/30 rounded transition-colors text-sm font-semibold text-gray-300">
                        Details
                      </button>
                    </div>
                  </div>
                  <div className="text-xs text-gray-500 py-2">{item.source.toUpperCase()}</div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </DashboardLayout>
  );
}

interface MediaCardProps {
  item: MediaItem;
}

function MediaCard({ item }: MediaCardProps) {
  return (
    <div className="group cursor-pointer rounded-lg overflow-hidden bg-black/50 border border-cyan-500/10 hover:border-cyan-500/30 transition-colors">
      <div className="relative overflow-hidden h-64">
        <img
          src={item.poster}
          alt={item.title}
          className="w-full h-full object-cover group-hover:scale-110 transition-transform duration-300"
        />
        <div className="absolute inset-0 bg-black/40 opacity-0 group-hover:opacity-100 transition-opacity flex items-center justify-center">
          <Play className="text-white fill-white" size={48} />
        </div>
        <div className="absolute top-2 right-2 bg-black/80 px-2 py-1 rounded text-xs font-semibold text-yellow-400">
           {item.rating}
        </div>
      </div>
      <div className="p-4">
        <h3 className="font-semibold text-cyan-300 mb-2">{item.title}</h3>
        <div className="space-y-2">
          <div className="bg-gray-700 h-1 rounded-full overflow-hidden">
            <div
              className="bg-gradient-to-r from-cyan-500 to-purple-500 h-full"
              style={{ width: `${item.progress}%` }}
            />
          </div>
          <div className="flex justify-between text-xs text-gray-400">
            <span>{item.progress}% watched</span>
            <span>{item.source}</span>
          </div>
        </div>
      </div>
    </div>
  );
}

'use client';

import React, { ReactNode } from 'react';
import { useTheme } from '@/context/ThemeContext';

interface HeroProps {
  title: string;
  subtitle?: string;
  description?: string;
  image?: string;
  actions?: ReactNode;
  background?: 'gradient' | 'solid' | 'glass';
  imagePosition?: 'left' | 'right' | 'center';
}

/**
 * Premium Hero Section Component
 * Eye-catching hero section for landing pages and key sections
 */
export const Hero: React.FC<HeroProps> = ({
  title,
  subtitle,
  description,
  image,
  actions,
  background = 'gradient',
  imagePosition = 'right',
}) => {
  const { currentTheme } = useTheme();

  const isImageOnRight = imagePosition === 'right';
  const isImageCenter = imagePosition === 'center';

  return (
    <section className={`relative overflow-hidden py-12 md:py-20 lg:py-28`}>
      {/* Background Effects */}
      <div
        className="absolute -top-40 -right-40 w-80 h-80 rounded-full blur-3xl opacity-20 pointer-events-none"
        style={{
          backgroundColor: currentTheme.colors.primary,
        }}
      />
      <div
        className="absolute -bottom-40 -left-40 w-80 h-80 rounded-full blur-3xl opacity-20 pointer-events-none"
        style={{
          backgroundColor: currentTheme.colors.accent,
        }}
      />

      <div className="relative max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div
          className={`
            grid grid-cols-1 ${!isImageCenter ? 'md:grid-cols-2' : ''} gap-8 lg:gap-12 items-center
          `}
        >
          {/* Content */}
          <div
            className={`
              order-1
              ${isImageOnRight && image ? 'md:order-1' : ''}
              ${isImageOnRight && !image ? 'md:col-span-2' : ''}
            `}
          >
            {subtitle && (
              <div className="inline-block mb-4">
                <span
                  className="inline-block px-4 py-2 rounded-full text-xs font-semibold uppercase tracking-wider"
                  style={{
                    backgroundColor: `${currentTheme.colors.primary}15`,
                    color: currentTheme.colors.primary,
                  }}
                >
                  {subtitle}
                </span>
              </div>
            )}

            <h1 className="text-4xl md:text-5xl lg:text-6xl font-serif font-bold text-text mb-6 leading-tight">
              {title}
            </h1>

            {description && (
              <p className="text-lg md:text-xl text-text-secondary mb-8 leading-relaxed max-w-xl">
                {description}
              </p>
            )}

            {actions && (
              <div className="flex flex-col sm:flex-row gap-4">
                {actions}
              </div>
            )}
          </div>

          {/* Image */}
          {image && !isImageCenter && (
            <div
              className={`
                order-2 relative
                ${isImageOnRight ? 'md:order-2' : 'md:order-1'}
              `}
            >
              <div className="relative z-10">
                <img
                  src={image}
                  alt="Hero visual"
                  className="w-full h-auto rounded-lg shadow-premium object-cover"
                />
              </div>
              {/* Image Border Glow */}
              <div
                className="absolute inset-0 rounded-lg blur-xl -z-10"
                style={{
                  background: `linear-gradient(135deg, ${currentTheme.colors.primary}40, ${currentTheme.colors.accent}40)`,
                }}
              />
            </div>
          )}
        </div>

        {/* Center Image (optional) */}
        {image && isImageCenter && (
          <div className="mt-12 relative">
            <div className="relative z-10">
              <img
                src={image}
                alt="Hero visual"
                className="w-full h-auto rounded-lg shadow-premium object-cover max-h-96"
              />
            </div>
            <div
              className="absolute inset-0 rounded-lg blur-xl -z-10"
              style={{
                background: `linear-gradient(135deg, ${currentTheme.colors.primary}40, ${currentTheme.colors.accent}40)`,
              }}
            />
          </div>
        )}
      </div>
    </section>
  );
};

export default Hero;

'use client';

import { useState, useRef, useEffect } from 'react';
import { Loader2, Mic, MicOff, Volume2 } from 'lucide-react';

type VoiceMode = 'browser' | 'whisper';

interface VoiceInputProps {
  onTranscript: (text: string) => void;
  placeholder?: string;
  mode?: VoiceMode;
}

export function VoiceInput({
  onTranscript,
  placeholder = 'Click mic to speak...',
  mode = 'browser',
}: VoiceInputProps) {
  const [isListening, setIsListening] = useState(false);
  const [transcript, setTranscript] = useState('');
  const [error, setError] = useState('');
  const [isTranscribing, setIsTranscribing] = useState(false);
  const [currentMode, setCurrentMode] = useState<VoiceMode>(mode);
  const [supportedModes, setSupportedModes] = useState<VoiceMode[]>([]);

  const recognitionRef = useRef<any>(null);
  const mediaRecorderRef = useRef<MediaRecorder | null>(null);
  const audioChunksRef = useRef<Blob[]>([]);

  useEffect(() => {
    const modes: VoiceMode[] = [];

    // Check browser speech support
    const SpeechRecognition =
      (window as any).webkitSpeechRecognition || (window as any).SpeechRecognition;
    if (SpeechRecognition) {
      modes.push('browser');
      recognitionRef.current = new SpeechRecognition();
      recognitionRef.current.continuous = false;
      recognitionRef.current.interimResults = true;
      recognitionRef.current.lang = 'en-US';

      recognitionRef.current.onstart = () => setIsListening(true);
      recognitionRef.current.onend = () => setIsListening(false);
      recognitionRef.current.onerror = (event: any) => {
        setError(`Browser error: ${event.error}`);
        setIsListening(false);
      };
      recognitionRef.current.onresult = (event: any) => {
        let interimTranscript = '';
        for (let i = event.resultIndex; i < event.results.length; i++) {
          const transcript = event.results[i][0].transcript;
          if (event.results[i].isFinal) {
            setTranscript((prev) => prev + transcript + ' ');
            onTranscript(transcript);
          } else {
            interimTranscript += transcript;
          }
        }
        if (interimTranscript) {
          setTranscript((prev) => prev.split(' ').slice(0, -1).join(' ') + ' ' + interimTranscript);
        }
      };
    }

    // Check Whisper support (requires recording capability)
    if (navigator.mediaDevices && navigator.mediaDevices.getUserMedia) {
      modes.push('whisper');
    }

    setSupportedModes(modes);

    // Set initial mode to first available
    if (modes.length > 0 && !modes.includes(mode)) {
      setCurrentMode(modes[0]);
    }
  }, [mode, onTranscript]);

  const startBrowserRecording = () => {
    if (!recognitionRef.current) {
      setError('Speech recognition not supported in this browser');
      return;
    }
    setError('');
    setTranscript('');
    recognitionRef.current.start();
  };

  const stopBrowserRecording = () => {
    if (recognitionRef.current) {
      recognitionRef.current.stop();
    }
  };

  const startWhisperRecording = async () => {
    try {
      setError('');
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const mediaRecorder = new MediaRecorder(stream);
      mediaRecorderRef.current = mediaRecorder;
      audioChunksRef.current = [];

      mediaRecorder.ondataavailable = (event) => {
        audioChunksRef.current.push(event.data);
      };

      mediaRecorder.onstop = async () => {
        const audioBlob = new Blob(audioChunksRef.current, { type: 'audio/wav' });
        await sendToWhisper(audioBlob);
        stream.getTracks().forEach((track) => track.stop());
      };

      mediaRecorder.start();
      setIsListening(true);
    } catch (err: any) {
      setError(`Microphone access denied: ${err.message}`);
    }
  };

  const stopWhisperRecording = () => {
    if (mediaRecorderRef.current && isListening) {
      mediaRecorderRef.current.stop();
      setIsListening(false);
    }
  };

  const sendToWhisper = async (audioBlob: Blob) => {
    setIsTranscribing(true);
    try {
      const formData = new FormData();
      formData.append('audio', audioBlob, 'audio.wav');

      const response = await fetch('/api/transcribe', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Transcription failed');
      }

      const data = await response.json();
      setTranscript(data.text);
      onTranscript(data.text);
    } catch (err: any) {
      setError(`Whisper error: ${err.message}`);
    } finally {
      setIsTranscribing(false);
    }
  };

  const toggleRecording = () => {
    if (currentMode === 'browser') {
      if (isListening) {
        stopBrowserRecording();
      } else {
        startBrowserRecording();
      }
    } else {
      if (isListening) {
        stopWhisperRecording();
      } else {
        startWhisperRecording();
      }
    }
  };

  return (
    <div className="w-full max-w-2xl mx-auto space-y-4">
      {/* Mode Selector */}
      {supportedModes.length > 1 && (
        <div className="flex gap-2">
          {supportedModes.map((m) => (
            <button
              key={m}
              onClick={() => setCurrentMode(m)}
              className={`px-3 py-1 rounded text-sm font-medium transition ${
                currentMode === m
                  ? 'bg-blue-600 text-white'
                  : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
              }`}
              disabled={isListening || isTranscribing}
            >
              {m === 'browser' ? 'Browser Speech' : 'Whisper'}
            </button>
          ))}
        </div>
      )}

      {/* Main Voice Input */}
      <div className="border-2 border-gray-300 rounded-lg p-6 space-y-4">
        <button
          onClick={toggleRecording}
          disabled={isTranscribing}
          className={`w-full py-4 rounded-lg font-bold text-white transition flex items-center justify-center gap-2 ${
            isListening
              ? 'bg-red-600 hover:bg-red-700'
              : 'bg-blue-600 hover:bg-blue-700'
          } ${isTranscribing ? 'opacity-50 cursor-not-allowed' : ''}`}
        >
          {isTranscribing ? (
            <>
              <Loader2 className="w-5 h-5 animate-spin" />
              Transcribing...
            </>
          ) : isListening ? (
            <>
              <MicOff className="w-5 h-5" />
              Stop Recording
            </>
          ) : (
            <>
              <Mic className="w-5 h-5" />
              Start Recording
            </>
          )}
        </button>

        {/* Transcript Display */}
        {transcript && (
          <div className="bg-blue-50 border border-blue-200 rounded p-4">
            <p className="text-sm text-gray-600 mb-1">Transcript:</p>
            <p className="text-lg text-gray-900">{transcript}</p>
          </div>
        )}

        {/* Error Display */}
        {error && (
          <div className="bg-red-50 border border-red-200 rounded p-4">
            <p className="text-sm text-red-700">{error}</p>
          </div>
        )}

        {/* Status Indicator */}
        {isListening && (
          <div className="flex items-center gap-2 text-sm text-orange-600">
            <Volume2 className="w-4 h-4 animate-pulse" />
            Listening... ({currentMode === 'browser' ? 'Browser' : 'Whisper'})
          </div>
        )}
      </div>

      {/* Mode Info */}
      <div className="text-xs text-gray-600 bg-gray-50 p-3 rounded">
        <p className="font-semibold mb-1">Current Mode: {currentMode === 'browser' ? 'Browser Speech API' : 'OpenAI Whisper'}</p>
        {currentMode === 'browser' && (
          <p>Works in Chrome/Edge/Safari. Less accurate but no API key needed.</p>
        )}
        {currentMode === 'whisper' && (
          <p>Requires OPENAI_API_KEY in .env.local. More accurate transcription.</p>
        )}
      </div>
    </div>
  );
}

'use client';

import React, { ButtonHTMLAttributes, ReactNode } from 'react';
import { useTheme } from '@/context/ThemeContext';

export interface ButtonProps extends ButtonHTMLAttributes<HTMLButtonElement> {
  variant?: 'primary' | 'secondary' | 'outline' | 'ghost' | 'danger';
  size?: 'xs' | 'sm' | 'md' | 'lg' | 'xl';
  isLoading?: boolean;
  icon?: ReactNode;
  iconPosition?: 'left' | 'right';
  fullWidth?: boolean;
}

const sizeClasses = {
  xs: 'px-3 py-1.5 text-xs rounded-sm',
  sm: 'px-4 py-2 text-sm rounded-base',
  md: 'px-6 py-2.5 text-base rounded-md',
  lg: 'px-8 py-3 text-lg rounded-lg',
  xl: 'px-10 py-4 text-xl rounded-lg',
};

/**
 * Premium Button Component
 * Supports multiple variants and sizes with premium styling
 * Integrates with theme system for automatic color application
 */
export const Button: React.FC<ButtonProps> = ({
  variant = 'primary',
  size = 'md',
  isLoading = false,
  icon,
  iconPosition = 'left',
  fullWidth = false,
  disabled,
  className = '',
  children,
  ...props
}) => {
  const { currentTheme } = useTheme();

  const baseClasses =
    'inline-flex items-center justify-center font-medium transition-all duration-base font-accent';

  const variantClasses = {
    primary: 'bg-primary text-background hover:bg-primary-light hover:shadow-lg active:scale-95 disabled:opacity-50',
    secondary:
      'bg-surface border border-primary text-primary hover:bg-primary hover:text-background hover:shadow-lg active:scale-95',
    outline:
      'border-2 border-primary text-primary hover:bg-primary hover:text-background active:scale-95',
    ghost:
      'text-primary hover:bg-primary hover:text-background active:scale-95 hover:shadow-md',
    danger:
      'bg-error text-white hover:bg-red-600 hover:shadow-lg active:scale-95 disabled:opacity-50',
  };

  const widthClass = fullWidth ? 'w-full' : '';

  const finalClasses = `
    ${baseClasses}
    ${sizeClasses[size]}
    ${variantClasses[variant]}
    ${widthClass}
    ${disabled ? 'cursor-not-allowed opacity-50' : ''}
    ${className}
  `;

  return (
    <button
      className={finalClasses.trim()}
      disabled={disabled || isLoading}
      {...props}
    >
      {isLoading ? (
        <>
          <svg
            className="mr-2 h-4 w-4 animate-spin"
            xmlns="http://www.w3.org/2000/svg"
            fill="none"
            viewBox="0 0 24 24"
          >
            <circle
              className="opacity-25"
              cx="12"
              cy="12"
              r="10"
              stroke="currentColor"
              strokeWidth="4"
            />
            <path
              className="opacity-75"
              fill="currentColor"
              d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
            />
          </svg>
          Loading...
        </>
      ) : (
        <>
          {icon && iconPosition === 'left' && <span className="mr-2">{icon}</span>}
          {children}
          {icon && iconPosition === 'right' && <span className="ml-2">{icon}</span>}
        </>
      )}
    </button>
  );
};

export default Button;

/**
 *  Advanced Analytics & Insights Engine
 * User analytics, content performance, revenue tracking, cohort analysis
 */

import { db as clientDb } from './firebase';
import {
  collection,
  query,
  where,
  getDocs,
  doc,
  setDoc,
  updateDoc,
  increment,
  Timestamp,
  writeBatch,
} from 'firebase/firestore';

export interface UserInsights {
  userId: string;
  date: string;
  generationsCount: number;
  dmRepliesCount: number;
  moneyPlaysCount: number;
  imageGenerationsCount: number;
  videoGenerationsCount: number;
  totalTokensUsed: number;
  apiCallsCount: number;
  averageResponseTime: number; // ms
  errorRate: number; // percentage
}

export interface ContentPerformance {
  contentId: string;
  userId: string;
  contentType: string;
  title: string;
  views: number;
  shares: number;
  saves: number;
  engagement: number; // percentage
  sentiment: 'positive' | 'neutral' | 'negative';
  createdAt: Date;
  updatedAt: Date;
}

export interface RevenueMetrics {
  month: string;
  userId: string;
  totalRevenue: number;
  subscriptionRevenue: number;
  affiliateRevenue: number;
  addonRevenue: number;
  transactionCount: number;
  mrr: number; // Monthly recurring revenue
  churnRate: number; // percentage
}

/**
 * Track user insights
 */
export async function trackUserInsights(
  userId: string,
  metrics: Partial<UserInsights>
): Promise<void> {
  if (!clientDb) throw new Error('Firebase not initialized');

  const today = new Date().toISOString().split('T')[0];
  const insightsRef = doc(clientDb, 'userInsights', `${userId}_${today}`);

  const batch = writeBatch(clientDb);
  batch.set(insightsRef, {
    userId,
    date: today,
    generationsCount: metrics.generationsCount || 0,
    dmRepliesCount: metrics.dmRepliesCount || 0,
    moneyPlaysCount: metrics.moneyPlaysCount || 0,
    imageGenerationsCount: metrics.imageGenerationsCount || 0,
    videoGenerationsCount: metrics.videoGenerationsCount || 0,
    totalTokensUsed: metrics.totalTokensUsed || 0,
    apiCallsCount: metrics.apiCallsCount || 0,
    averageResponseTime: metrics.averageResponseTime || 0,
    errorRate: metrics.errorRate || 0,
  });

  await batch.commit();
}

/**
 * Get user insights for date range
 */
export async function getUserInsightsRange(
  userId: string,
  startDate: Date,
  endDate: Date
): Promise<UserInsights[]> {
  if (!clientDb) throw new Error('Firebase not initialized');

  const insightsRef = collection(clientDb, 'userInsights');
  const q = query(insightsRef, where('userId', '==', userId));

  const snapshot = await getDocs(q);
  const results: UserInsights[] = [];

  snapshot.docs.forEach(doc => {
    const data = doc.data() as UserInsights;
    const docDate = new Date(data.date);

    if (docDate >= startDate && docDate <= endDate) {
      results.push(data);
    }
  });

  return results.sort((a, b) => new Date(a.date).getTime() - new Date(b.date).getTime());
}

/**
 * Track content performance
 */
export async function trackContentPerformance(
  contentId: string,
  userId: string,
  metrics: Partial<ContentPerformance>
): Promise<ContentPerformance> {
  if (!clientDb) throw new Error('Firebase not initialized');

  const performanceRef = doc(clientDb, 'contentPerformance', contentId);
  const contentPerformance: ContentPerformance = {
    contentId,
    userId,
    contentType: metrics.contentType || 'unknown',
    title: metrics.title || 'Untitled',
    views: metrics.views || 0,
    shares: metrics.shares || 0,
    saves: metrics.saves || 0,
    engagement: metrics.engagement || 0,
    sentiment: metrics.sentiment || 'neutral',
    createdAt: metrics.createdAt || new Date(),
    updatedAt: new Date(),
  };

  const batch = writeBatch(clientDb);
  batch.set(performanceRef, contentPerformance);
  await batch.commit();

  return contentPerformance;
}

/**
 * Get top performing content
 */
export async function getTopPerformingContent(
  userId: string,
  metric: 'views' | 'shares' | 'engagement' = 'views',
  limit = 10
): Promise<ContentPerformance[]> {
  if (!clientDb) throw new Error('Firebase not initialized');

  const performanceRef = collection(clientDb, 'contentPerformance');
  const q = query(performanceRef, where('userId', '==', userId));

  const snapshot = await getDocs(q);
  const content = snapshot.docs
    .map(doc => doc.data() as ContentPerformance)
    .sort((a, b) => {
      const aValue = a[metric] as number;
      const bValue = b[metric] as number;
      return bValue - aValue;
    })
    .slice(0, limit);

  return content;
}

/**
 * Track revenue metrics
 */
export async function trackRevenueMetrics(
  userId: string,
  month: string,
  data: Partial<RevenueMetrics>
): Promise<RevenueMetrics> {
  if (!clientDb) throw new Error('Firebase not initialized');

  const revenueRef = doc(clientDb, 'revenueMetrics', `${userId}_${month}`);
  const metrics: RevenueMetrics = {
    month,
    userId,
    totalRevenue: data.totalRevenue || 0,
    subscriptionRevenue: data.subscriptionRevenue || 0,
    affiliateRevenue: data.affiliateRevenue || 0,
    addonRevenue: data.addonRevenue || 0,
    transactionCount: data.transactionCount || 0,
    mrr: data.mrr || 0,
    churnRate: data.churnRate || 0,
  };

  const batch = writeBatch(clientDb);
  batch.set(revenueRef, metrics);
  await batch.commit();

  return metrics;
}

/**
 * Get revenue metrics for date range
 */
export async function getRevenueMetricsRange(
  userId: string,
  startMonth: string,
  endMonth: string
): Promise<RevenueMetrics[]> {
  if (!clientDb) throw new Error('Firebase not initialized');

  const revenueRef = collection(clientDb, 'revenueMetrics');
  const q = query(revenueRef, where('userId', '==', userId));

  const snapshot = await getDocs(q);
  const results = snapshot.docs
    .map(doc => doc.data() as RevenueMetrics)
    .filter(r => r.month >= startMonth && r.month <= endMonth)
    .sort((a, b) => a.month.localeCompare(b.month));

  return results;
}

/**
 * Get user cohort analysis
 */
export async function getUserCohortAnalysis(
  joinDateStart: Date,
  joinDateEnd: Date
): Promise<{
  cohortSize: number;
  activeUsers: number;
  retentionRate: number;
  avgRevenuePerUser: number;
  avgLTVEstimate: number;
}> {
  if (!clientDb) throw new Error('Firebase not initialized');

  const usersRef = collection(clientDb, 'users');
  const q = query(
    usersRef,
    where('createdAt', '>=', Timestamp.fromDate(joinDateStart)),
    where('createdAt', '<=', Timestamp.fromDate(joinDateEnd))
  );

  const snapshot = await getDocs(q);
  const cohortSize = snapshot.size;

  // Calculate retention (users active in last 30 days)
  const thirtyDaysAgo = new Date(Date.now() - 30 * 24 * 60 * 60 * 1000);
  const activeUsers = snapshot.docs.filter(doc => {
    const lastActive = doc.data().lastActivity?.toDate?.() || doc.data().createdAt?.toDate?.();
    return lastActive > thirtyDaysAgo;
  }).length;

  const retentionRate = cohortSize > 0 ? (activeUsers / cohortSize) * 100 : 0;

  // Calculate average revenue
  let totalRevenue = 0;
  let totalMRR = 0;

  for (const userDoc of snapshot.docs) {
    const userId = userDoc.id;
    const revenueRef = collection(clientDb, 'revenueMetrics');
    const revenueQ = query(revenueRef, where('userId', '==', userId));
    const revenueSnapshot = await getDocs(revenueQ);

    revenueSnapshot.docs.forEach(doc => {
      const data = doc.data() as RevenueMetrics;
      totalRevenue += data.totalRevenue || 0;
      totalMRR += data.mrr || 0;
    });
  }

  const avgRevenuePerUser = cohortSize > 0 ? totalRevenue / cohortSize : 0;
  const avgMRRPerUser = cohortSize > 0 ? totalMRR / cohortSize : 0;
  // LTV estimate: 12 months * MRR / churn rate (simplified)
  const avgLTVEstimate = avgMRRPerUser > 0 ? (avgMRRPerUser * 12) / Math.max(0.01, 0.05) : 0;

  return {
    cohortSize,
    activeUsers,
    retentionRate,
    avgRevenuePerUser,
    avgLTVEstimate,
  };
}

/**
 * Generate comprehensive analytics report
 */
export async function generateComprehensiveReport(
  userId: string,
  startDate: Date,
  endDate: Date
): Promise<{
  period: string;
  totalGenerations: number;
  totalInteractions: number;
  topContent: ContentPerformance[];
  totalRevenue: number;
  averageEngagement: number;
  insights: string[];
}> {
  const insights = await getUserInsightsRange(userId, startDate, endDate);
  const topContent = await getTopPerformingContent(userId, 'engagement', 5);

  const totalGenerations = insights.reduce((sum, i) => sum + i.generationsCount, 0);
  const totalInteractions =
    insights.reduce((sum, i) => sum + (i.dmRepliesCount + i.moneyPlaysCount), 0) +
    totalGenerations;

  const startMonth = startDate.toISOString().slice(0, 7);
  const endMonth = endDate.toISOString().slice(0, 7);
  const revenueData = await getRevenueMetricsRange(userId, startMonth, endMonth);
  const totalRevenue = revenueData.reduce((sum, r) => sum + r.totalRevenue, 0);

  const avgEngagement =
    topContent.length > 0
      ? topContent.reduce((sum, c) => sum + c.engagement, 0) / topContent.length
      : 0;

  // Generate insights
  const generatedInsights: string[] = [];
  if (totalGenerations > 1000) {
    generatedInsights.push(' You\'re a power user! 1000+ generations this period.');
  }
  if (avgEngagement > 60) {
    generatedInsights.push(' Excellent engagement! Your content is resonating with audiences.');
  }
  if (totalRevenue > 1000) {
    generatedInsights.push(' Strong revenue growth this period.');
  }

  return {
    period: `${startDate.toLocaleDateString()} - ${endDate.toLocaleDateString()}`,
    totalGenerations,
    totalInteractions,
    topContent,
    totalRevenue,
    averageEngagement: Math.round(avgEngagement),
    insights: generatedInsights,
  };
}

export default {
  trackUserInsights,
  getUserInsightsRange,
  trackContentPerformance,
  getTopPerformingContent,
  trackRevenueMetrics,
  getRevenueMetricsRange,
  getUserCohortAnalysis,
  generateComprehensiveReport,
};

'use client';

// Google Cloud Identity Platform (GCIP) Configuration
// This file manages auth providers and user sessions

import { 
  signInWithPopup, 
  signOut, 
  onAuthStateChanged, 
  GoogleAuthProvider,
  FacebookAuthProvider,
  TwitterAuthProvider,
  GithubAuthProvider,
  User,
  AuthProvider,
  signInWithEmailAndPassword,
  createUserWithEmailAndPassword,
  RecaptchaVerifier,
  multiFactor,
  PhoneMultiFactorGenerator,
  PhoneAuthProvider,
  PhoneAuthCredential
} from 'firebase/auth';
import { auth } from './firebase';

// Initialize all OAuth providers
const googleProvider = new GoogleAuthProvider();
const facebookProvider = new FacebookAuthProvider();
const twitterProvider = new TwitterAuthProvider();
const githubProvider = new GithubAuthProvider();

// Add scopes for enhanced data access
googleProvider.addScope('profile');
googleProvider.addScope('email');
facebookProvider.addScope('email');
twitterProvider.addScope('tweet.read');
githubProvider.addScope('user:email');

// Provider map for dynamic sign-in
export const providers: Record<string, AuthProvider> = {
  google: googleProvider,
  facebook: facebookProvider,
  twitter: twitterProvider,
  github: githubProvider,
};

// Sign in with OAuth provider
export async function signInWithProvider(providerName: string): Promise<User> {
  const provider = providers[providerName];
  if (!provider) throw new Error(`Unknown provider: ${providerName}`);
  if (!auth) throw new Error('Firebase auth not initialized');

  const result = await signInWithPopup(auth, provider);
  return result.user;
}

// Email/Password sign up
export async function signUpWithEmail(email: string, password: string): Promise<User> {
  if (!auth) throw new Error('Firebase auth not initialized');
  const result = await createUserWithEmailAndPassword(auth, email, password);
  return result.user;
}

// Email/Password sign in
export async function signInWithEmail(email: string, password: string): Promise<User> {
  if (!auth) throw new Error('Firebase auth not initialized');
  const result = await signInWithEmailAndPassword(auth, email, password);
  return result.user;
}

// Sign out
export async function signOutUser(): Promise<void> {
  if (!auth) throw new Error('Firebase auth not initialized');
  await signOut(auth);
}

// Monitor auth state changes
export function onAuthChange(callback: (user: User | null) => void): () => void {
  if (!auth) throw new Error('Firebase auth not initialized');
  return onAuthStateChanged(auth, callback);
}

// Get current user
export function getCurrentUser(): User | null {
  return auth?.currentUser ?? null;
}

// Setup MFA (Multi-Factor Authentication)
export async function enableMFAPhone(phoneNumber: string): Promise<string> {
  const user = getCurrentUser();
  if (!user) throw new Error('No authenticated user');
  if (!auth) throw new Error('Firebase auth not initialized');

  const recaptchaVerifier = new RecaptchaVerifier(auth, 'recaptcha-container', {
    size: 'invisible',
  });

  // const phoneAuthProvider = new PhoneAuthProvider(auth);
  const verificationId = await phoneAuthProvider.verifyPhoneNumber(
    phoneNumber,
    recaptchaVerifier
  );

  return verificationId;
}

// Complete MFA verification
export async function completeMFAVerification(verificationId: string, code: string): Promise<void> {
  const user = getCurrentUser();
  if (!user) throw new Error('No authenticated user');
  if (!auth) throw new Error('Firebase auth not initialized');

  // const phoneAuthProvider = new PhoneAuthProvider(auth as any);
  const credentials = PhoneAuthProvider.credential(verificationId, code);
  const multiFactorAssertion = PhoneMultiFactorGenerator.assertion(credentials);
  
  await multiFactor(user).enroll(multiFactorAssertion, 'My Phone');
}

// Link additional provider to existing account
export async function linkProviderToAccount(providerName: string): Promise<User> {
  const user = getCurrentUser();
  if (!user) throw new Error('No authenticated user');
  if (!auth) throw new Error('Firebase auth not initialized');

  const provider = providers[providerName];
  if (!provider) throw new Error(`Unknown provider: ${providerName}`);

  const result = await signInWithPopup(auth as any, provider);
  return result.user;
}

// Get user's auth providers
export function getLinkedProviders(): string[] {
  const user = getCurrentUser();
  if (!user) return [];
  
  return user.providerData.map(p => p.providerId);
}

// Delete account
export async function deleteUserAccount(): Promise<void> {
  const user = getCurrentUser();
  if (!user) throw new Error('No authenticated user');
  
  await user.delete();
}

// Check if user has MFA enabled
export function hasMFAEnabled(): boolean {
  const user = getCurrentUser();
  if (!user) return false;
  
  return multiFactor(user).enrolledFactors.length > 0;
}

