/**
 * Helper: Convert USD to crypto
 */
export function convertUsdToCrypto(
  usdAmount: number,
  asset: CryptoAsset,
  priceAnchor: number = PRICE_ANCHORS[asset]
): number {
  return usdAmount / priceAnchor;
}

/**
 * Helper: Convert crypto to USD
 */
export function convertCryptoToUsd(
  cryptoAmount: number,
  asset: CryptoAsset,
  priceAnchor: number = PRICE_ANCHORS[asset]
): number {
  return cryptoAmount * priceAnchor;
}

/**
 * Helper: Get minimum payment for tier/asset
 */
export function getMinimumCryptoPayment(
  tier: string,
  asset: CryptoAsset
): number | null {
  const tierPricing = TIER_CRYPTO_PRICING[tier];
  if (!tierPricing) return null;
  return tierPricing[asset];
}

/**
 * Helper: Get all crypto payment addresses for user
 */
export function getUserPaymentAddresses(wallet: UserCryptoWallet): Record<CryptoAsset, string> {
  const addresses: Record<string, string> = {};
  for (const [asset, info] of Object.entries(wallet.wallets)) {
    if (info?.isActive) {
      addresses[asset as CryptoAsset] = info.address;
    }
  }
  return addresses as Record<CryptoAsset, string>;
}

/**
 * Helper: Calculate trading fee
 */
export function calculateTradingFee(
  asset: CryptoAsset,
  amount: number,
  userTier: string = 'free'
): number {
  const baseFeePercent = {
    free: 0.05, // 5%
    starter: 0.03, // 3%
    creator: 0.02, // 2%
    pro: 0.015, // 1.5%
    elite: 0.01, // 1%
    agency: 0.005, // 0.5%
    enterprise: 0.003, // 0.3%
    god: 0, // Free
  };

  const feePercent = baseFeePercent[userTier as keyof typeof baseFeePercent] || 0.05;
  return amount * (feePercent / 100);
}

/**
 * Helper: Get marketplace commission
 */
export function getMarketplaceCommission(
  saleAmount: number,
  sellerTier: string
): { platform: number; seller: number } {
  const platformPercent = {
    free: 0.30, // 30% platform, 70% seller
    starter: 0.25, // 25% platform
    creator: 0.20, // 20% platform
    pro: 0.15,
    elite: 0.12,
    agency: 0.10,
    enterprise: 0.05,
    god: 0, // 0% platform, 100% seller
  };

  const platformPerc = platformPercent[sellerTier as keyof typeof platformPercent] || 0.30;
  const platformFee = saleAmount * platformPerc;
  const sellerRevenue = saleAmount - platformFee;

  return { platform: platformFee, seller: sellerRevenue };
}

/**
 * Helper: Validate wallet address format
 */
export function validateWalletAddress(address: string, asset: CryptoAsset): boolean {
  const patterns: Record<CryptoAsset, RegExp> = {
    BTC: /^(bc1|[13])[a-zA-HJ-NP-Z0-9]{25,39}$/, // Bech32, P2SH, P2PKH
    ETH: /^0x[a-fA-F0-9]{40}$/,
    USDC: /^0x[a-fA-F0-9]{40}$/, // ERC-20
    SOL: /^[1-9A-HJ-NP-Za-km-z]{43,44}$/, // Base58
    XRP: /^r[a-zA-Z0-9]{24,34}$/,
    DOGE: /^[13D][a-zA-km-zA-HJ-NP-Z0-9]{25,34}$/,
    MATIC: /^0x[a-fA-F0-9]{40}$/, // Same as ETH
  };

  const pattern = patterns[asset];
  return pattern ? pattern.test(address) : false;
}

/**
 * Helper: Estimate network fee
 */
export function estimateNetworkFee(
  asset: CryptoAsset,
  amount: number,
  priority: 'low' | 'normal' | 'high' = 'normal'
): number {
  const baseFees: Record<CryptoAsset, Record<string, number>> = {
    BTC: { low: 0.00001, normal: 0.00002, high: 0.0001 },
    ETH: { low: 0.001, normal: 0.003, high: 0.005 },
    USDC: { low: 0.001, normal: 0.003, high: 0.005 },
    SOL: { low: 0.00001, normal: 0.00002, high: 0.0001 },
    XRP: { low: 10, normal: 12, high: 20 },
    DOGE: { low: 0.01, normal: 0.1, high: 0.5 },
    MATIC: { low: 0.001, normal: 0.002, high: 0.005 },
  };

  return baseFees[asset]?.[priority] || 0;
}

// Google Cloud Identity Platform (GCIP) Configuration
// This file manages auth providers and user sessions

import { 
  signInWithPopup, 
  signOut, 
  onAuthStateChanged, 
  GoogleAuthProvider,
  FacebookAuthProvider,
  TwitterAuthProvider,
  GithubAuthProvider,
  User,
  AuthProvider,
  signInWithEmailAndPassword,
  createUserWithEmailAndPassword,
  RecaptchaVerifier,
  multiFactor,
  PhoneMultiFactorGenerator,
  PhoneAuthProvider,
  PhoneAuthCredential
} from 'firebase/auth';
import { auth } from './firebase';

if (!auth) {
  throw new Error('Firebase auth is not initialized');
}

// Initialize all OAuth providers
const googleProvider = new GoogleAuthProvider();
const facebookProvider = new FacebookAuthProvider();
const twitterProvider = new TwitterAuthProvider();
const githubProvider = new GithubAuthProvider();

// Add scopes for enhanced data access
googleProvider.addScope('profile');
googleProvider.addScope('email');
facebookProvider.addScope('email');
twitterProvider.addScope('tweet.read');
githubProvider.addScope('user:email');

// Provider map for dynamic sign-in
export const providers: Record<string, AuthProvider> = {
  google: googleProvider,
  facebook: facebookProvider,
  twitter: twitterProvider,
  github: githubProvider,
};

// Sign in with OAuth provider
export async function signInWithProvider(providerName: string): Promise<User> {
  const provider = providers[providerName];
  if (!provider) throw new Error(`Unknown provider: ${providerName}`);
  if (!auth) throw new Error('Firebase auth is not initialized');
  
  const result = await signInWithPopup(auth, provider);
  return result.user;
}

// Email/Password sign up
export async function signUpWithEmail(email: string, password: string): Promise<User> {
  if (!auth) throw new Error('Firebase auth is not initialized');
  const result = await createUserWithEmailAndPassword(auth, email, password);
  return result.user;
}

// Email/Password sign in
export async function signInWithEmail(email: string, password: string): Promise<User> {
  if (!auth) throw new Error('Firebase auth is not initialized');
  const result = await signInWithEmailAndPassword(auth, email, password);
  return result.user;
}

// Sign out
export async function signOutUser(): Promise<void> {
  if (!auth) throw new Error('Firebase auth is not initialized');
  await signOut(auth);
}

// Monitor auth state changes
export function onAuthChange(callback: (user: User | null) => void): () => void {
  if (!auth) throw new Error('Firebase auth is not initialized');
  return onAuthStateChanged(auth, callback);
}

// Get current user
export function getCurrentUser(): User | null {
  if (!auth) throw new Error('Firebase auth is not initialized');
  return auth.currentUser;
}

// Setup MFA (Multi-Factor Authentication)
export async function enableMFAPhone(phoneNumber: string): Promise<string> {
  if (!auth) throw new Error('Firebase auth is not initialized');
  const user = getCurrentUser();
  if (!user) throw new Error('No authenticated user');

  const recaptchaVerifier = new RecaptchaVerifier(auth, 'recaptcha-container', {
    size: 'invisible',
  });

  const phoneAuthProvider = new PhoneAuthProvider(auth);
  const verificationId = await phoneAuthProvider.verifyPhoneNumber(
    phoneNumber,
    recaptchaVerifier
  );

  return verificationId;
}

// Complete MFA verification
export async function completeMFAVerification(verificationId: string, code: string): Promise<void> {
  const user = getCurrentUser();
  if (!user) throw new Error('No authenticated user');
  if (!auth) throw new Error('Firebase auth is not initialized');

  // PhoneAuthCredential.credential is deprecated, use proper MFA enrollment
  // This function requires server-side verification for security
  try {
    // Create a temporary credential object for assertion
    const credential = { verificationId, verificationCode: code } as any;
    const multiFactorAssertion = PhoneMultiFactorGenerator.assertion(credential as any);
    
    await multiFactor(user).enroll(multiFactorAssertion, 'My Phone');
  } catch (error) {
    // In production, phone MFA verification should be handled server-side
    throw new Error('MFA enrollment requires server-side verification');
  }
}

// Link additional provider to existing account
export async function linkProviderToAccount(providerName: string): Promise<User> {
  const user = getCurrentUser();
  if (!user) throw new Error('No authenticated user');
  if (!auth) throw new Error('Firebase auth is not initialized');

  const provider = providers[providerName];
  if (!provider) throw new Error(`Unknown provider: ${providerName}`);

  const result = await signInWithPopup(auth, provider);
  // Note: In real implementation, you'd use linkWithCredential
  return result.user;
}

// Get user's auth providers
export function getLinkedProviders(): string[] {
  const user = getCurrentUser();
  if (!user) return [];
  
  return user.providerData.map(p => p.providerId);
}

// Delete account
export async function deleteUserAccount(): Promise<void> {
  const user = getCurrentUser();
  if (!user) throw new Error('No authenticated user');
  
  await user.delete();
}

// Check if user has MFA enabled
export function hasMFAEnabled(): boolean {
  const user = getCurrentUser();
  if (!user) return false;
  
  return multiFactor(user).enrolledFactors.length > 0;
}

