/**
 * Calculate churn risk score for user
 */
export function calculateChurnRiskScore(profile: Partial<UserMonetizationProfile>): number {
  let score = 0;

  // Days inactive (max 40 points)
  if (profile.churnIndicators?.daysInactive) {
    const daysInactive = Math.min(profile.churnIndicators.daysInactive, 30);
    score += (daysInactive / 30) * 40;
  }

  // Failed payments (max 20 points)
  if (profile.churnIndicators?.failedPayments) {
    score += Math.min(profile.churnIndicators.failedPayments * 10, 20);
  }

  // Refund requests (max 15 points)
  if (profile.churnIndicators?.refundRequests) {
    score += Math.min(profile.churnIndicators.refundRequests * 7.5, 15);
  }

  // Support tickets (max 10 points)
  if (profile.churnIndicators?.supportTickets) {
    score += Math.min(profile.churnIndicators.supportTickets * 2, 10);
  }

  // Negative reviews (max 15 points)
  if (profile.churnIndicators?.negativeReviews) {
    score += Math.min(profile.churnIndicators.negativeReviews * 7.5, 15);
  }

  return Math.min(Math.round(score), 100);
}

/**
 * Calculate engagement score
 */
export function calculateEngagementScore(profile: Partial<UserMonetizationProfile>): number {
  let score = 0;

  // Usage intensity (max 50 points)
  if (profile.usageMetrics) {
    const aiUsage = (profile.usageMetrics.aiGenerationsUsed / profile.usageMetrics.aiGenerationsLimit) * 0.3;
    const storageUsage = (profile.usageMetrics.storageUsedGb / profile.usageMetrics.storageLimit) * 0.3;
    const projectsUsage = (profile.usageMetrics.projectsCreated / profile.usageMetrics.projectsLimit) * 0.4;
    score += (aiUsage + storageUsage + projectsUsage) * 50;
  }

  // Purchase history (max 30 points)
  if (profile.purchaseHistory?.totalPurchases) {
    score += Math.min(profile.purchaseHistory.totalPurchases * 5, 30);
  }

  // Account age & activity (max 20 points)
  if (profile.churnIndicators?.daysInactive !== undefined) {
    const activePercent = Math.max(0, (30 - profile.churnIndicators.daysInactive) / 30);
    score += activePercent * 20;
  }

  return Math.min(Math.round(score), 100);
}

/**
 * Calculate LTV (Lifetime Value) estimate
 */
export function calculateLTV(profile: Partial<UserMonetizationProfile>): number {
  const monthlySpend = profile.monthlySpend || 0;
  const retentionRate = Math.max(0.5, 1 - (profile.churnIndicators?.daysInactive || 0) / 120);
  const expectedMonths = Math.log(0.05) / Math.log(retentionRate); // Until 95% churn
  
  return monthlySpend * expectedMonths;
}

/**
 * Get personalized monetization recommendations
 */
export function getMonetizationRecommendations(
  profile: UserMonetizationProfile
): {
  tier?: TierName;
  upsells: string[];
  offer?: {
    type: string;
    discount: number;
    validDays: number;
  };
} {
  const recommendations: any = { upsells: [] };

  // Tier recommendation based on usage
  const usagePercentage = Math.max(
    profile.usageMetrics.aiGenerationsUsed / profile.usageMetrics.aiGenerationsLimit,
    profile.usageMetrics.storageUsedGb / profile.usageMetrics.storageLimit,
    profile.usageMetrics.projectsCreated / profile.usageMetrics.projectsLimit
  );

  if (usagePercentage > 0.8 && profile.currentTier !== 'god') {
    const nextTier = Object.values(UPGRADE_INCENTIVES)
      .find(tier => tier.recommendedUpgrade === profile.currentTier);
    if (nextTier) {
      recommendations.tier = nextTier.recommendedUpgrade;
    }
  }

  // Upsell recommendations from triggers
  for (const trigger of USAGE_BASED_UPSELL_TRIGGERS) {
    if (trigger.condition(profile)) {
      recommendations.upsells.push(...trigger.recommendedUpsells);
    }
  }

  // Churn retention offer
  const churnScore = profile.churnRiskScore;
  if (churnScore > 60) {
    if (churnScore < 70) {
      recommendations.offer = {
        type: 'loyalty_bonus',
        discount: 25,
        validDays: 7,
      };
    } else if (churnScore < 85) {
      recommendations.offer = {
        type: 'last_chance',
        discount: 50,
        validDays: 3,
      };
    } else {
      recommendations.offer = {
        type: 'vip_retention',
        discount: 60,
        validDays: 1,
      };
    }
  }

  return recommendations;
}

/**
 * Generate dynamic checkout copy with social proof
 */
export function generateCheckoutCopy(tier: TierName): {
  headline: string;
  subheading: string;
  ctaText: string;
  socialProof: string[];
} {
  const tierInfo = PREMIUM_TIERS[tier];
  const proof = SOCIAL_PROOF_MESSAGES.tierPopularity[tier as keyof typeof SOCIAL_PROOF_MESSAGES.tierPopularity];

  return {
    headline: `Join the ${tierInfo.displayName} tier`,
    subheading: tierInfo.subtitle,
    ctaText: `Get ${tierInfo.displayName} - $${tierInfo.monthlyPrice}/mo`,
    socialProof: [
      proof || 'Join thousands of creators',
      'No credit card required',
      'Cancel anytime',
      '24-hour money-back guarantee',
    ],
  };
}

/**
 * Calculate true annual value (with all discounts & offers)
 */
export function calculateAnnualValue(
  monthlyPrice: number,
  activeUpsells: number = 0,
  discountPercent: number = 0,
  includeAnnualDiscount: boolean = false
): number {
  const annualBase = monthlyPrice * 12;
  let total = annualBase;

  // Add upsells (assuming average upsell = $50/mo)
  total += activeUpsells * 50 * 12;

  // Apply discount
  if (discountPercent > 0) {
    total = total * (1 - discountPercent / 100);
  }

  // Apply annual discount (25% savings)
  if (includeAnnualDiscount) {
    total = total * 0.75;
  }

  return Math.round(total);
}

/**
 *  Server Initialization System
 * Handles all startup tasks, API validation, and service initialization
 */

import './config';
import { initializeApp as initializeFirebase } from 'firebase/app';
import { GoogleGenerativeAI } from '@google/generative-ai';
import OpenAI from 'openai';
import { captureMessage } from './sentry';

/**
 * Service initialization status
 */
export interface InitializationStatus {
  timestamp: Date;
  firebase: { initialized: boolean; error?: string };
  googleAI: { initialized: boolean; error?: string };
  openAI: { initialized: boolean; error?: string };
  stripe: { initialized: boolean; error?: string };
  nats: { initialized: boolean; error?: string };
  redis: { initialized: boolean; error?: string };
  email: { initialized: boolean; error?: string };
  allHealthy: boolean;
}

class ServerInitializer {
  private static instance: ServerInitializer;
  private initialized = false;
  private initializationStatus: InitializationStatus = {
    timestamp: new Date(),
    firebase: { initialized: false },
    googleAI: { initialized: false },
    openAI: { initialized: false },
    stripe: { initialized: false },
    nats: { initialized: false },
    redis: { initialized: false },
    email: { initialized: false },
    allHealthy: false,
  };

  private googleAI: GoogleGenerativeAI | null = null;
  private openAIClient: OpenAI | null = null;

  /**
   * Singleton pattern
   */
  static getInstance(): ServerInitializer {
    if (!ServerInitializer.instance) {
      ServerInitializer.instance = new ServerInitializer();
    }
    return ServerInitializer.instance;
  }

  /**
   * Initialize all services on startup
   */
  async initialize(): Promise<InitializationStatus> {
    if (this.initialized) {
      console.log(' Server already initialized');
      return this.initializationStatus;
    }

    console.log('\n Starting LitLabs Server Initialization...\n');
    
    const config = getConfig();
    
    // Run all initialization tasks in parallel
    await Promise.all([
      this.initializeFirebase(config),
      this.initializeGoogleAI(config),
      this.initializeOpenAI(config),
      this.initializeStripe(config),
      this.initializeNATS(config),
      this.initializeRedis(config),
      this.initializeEmail(config),
    ]);

    // Determine overall health
    // const services removed - unused

    const criticalServices = ['firebase', 'googleAI', 'stripe'];
    const allCriticalHealthy = criticalServices.every(
      service => this.initializationStatus[service as keyof Omit<InitializationStatus, 'timestamp' | 'allHealthy'>].initialized
    );

    this.initializationStatus.allHealthy = allCriticalServices && allCriticalHealthy;
    this.initializationStatus.timestamp = new Date();

    this.logInitializationStatus();

    this.initialized = true;
    return this.initializationStatus;
  }

  /**
   * Initialize Firebase
   */
  private async initializeFirebase(config: any): Promise<void> {
    try {
      // Firebase initialization is handled by the client/admin libraries
      // Just verify configuration is present
      if (!config.firebase.projectId || !config.firebase.apiKey) {
        throw new Error('Firebase configuration incomplete');
      }

      this.initializationStatus.firebase.initialized = true;
      console.log(' Firebase initialized');
    } catch (error) {
      const msg = error instanceof Error ? error.message : String(error);
      this.initializationStatus.firebase.error = msg;
      console.error(' Firebase initialization failed:', msg);
      captureException(error, 'firebase_init_error');
    }
  }

  /**
   * Initialize Google Generative AI (Gemini)
   */
  private async initializeGoogleAI(config: any): Promise<void> {
    try {
      if (!config.google.generativeAI) {
        throw new Error('GOOGLE_GENERATIVE_AI_API_KEY not configured');
      }

      this.googleAI = new GoogleGenerativeAI(config.google.generativeAI);

      // Test the API connection
      const model = this.googleAI.getGenerativeModel({ model: 'gemini-1.5-flash' });
      await model.generateContent('Test');

      this.initializationStatus.googleAI.initialized = true;
      console.log(' Google Generative AI (Gemini) initialized');
    } catch (error) {
      const msg = error instanceof Error ? error.message : String(error);
      this.initializationStatus.googleAI.error = msg;
      console.error(' Google AI initialization failed:', msg);
      captureException(error, 'google_ai_init_error');
    }
  }

  /**
   * Initialize OpenAI (ChatGPT, GPT-4)
   */
  private async initializeOpenAI(config: any): Promise<void> {
    try {
      if (!config.openai.apiKey) {
        console.warn('  OpenAI not configured (optional)');
        return;
      }

      this.openAIClient = new OpenAI({
        apiKey: config.openai.apiKey,
        organization: config.openai.orgId,
      });

      // Test the API connection
      await this.openAIClient.models.list();

      this.initializationStatus.openAI.initialized = true;
      console.log(' OpenAI (ChatGPT/GPT-4) initialized');
    } catch (error) {
      const msg = error instanceof Error ? error.message : String(error);
      this.initializationStatus.openAI.error = msg;
      console.warn('  OpenAI initialization failed (optional):', msg);
    }
  }

  /**
   * Initialize Stripe
   */
  private async initializeStripe(config: any): Promise<void> {
    try {
      if (!config.stripe.secret || !config.stripe.publishable) {
        throw new Error('Stripe keys not configured');
      }

      // Stripe initialization is handled by getStripe() in lib/stripe.ts
      // Just verify configuration
      if (!config.stripe.prices.pro || !config.stripe.prices.starter) {
        console.warn('  Some Stripe price IDs not configured');
      }

      this.initializationStatus.stripe.initialized = true;
      console.log(' Stripe initialized');
    } catch (error) {
      const msg = error instanceof Error ? error.message : String(error);
      this.initializationStatus.stripe.error = msg;
      console.error(' Stripe initialization failed:', msg);
      captureException(error, 'stripe_init_error');
    }
  }

  /**
   * Initialize NATS (Message Queue)
   */
  private async initializeNATS(config: any): Promise<void> {
    try {
      if (!config.messaging.nats.url) {
        console.warn('  NATS not configured (optional)');
        return;
      }

      // NATS connection is lazy-initialized when needed
      console.log(' NATS configured (lazy initialization)');
      this.initializationStatus.nats.initialized = true;
    } catch (error) {
      const msg = error instanceof Error ? error.message : String(error);
      this.initializationStatus.nats.error = msg;
      console.warn('  NATS configuration incomplete:', msg);
    }
  }

  /**
   * Initialize Redis (Cache/Rate Limiting)
   */
  private async initializeRedis(config: any): Promise<void> {
    try {
      if (!config.messaging.redis.url) {
        console.warn('  Redis not configured (optional)');
        return;
      }

      // Redis connection is lazy-initialized when needed
      console.log(' Redis configured (lazy initialization)');
      this.initializationStatus.redis.initialized = true;
    } catch (error) {
      const msg = error instanceof Error ? error.message : String(error);
      this.initializationStatus.redis.error = msg;
      console.warn('  Redis configuration incomplete:', msg);
    }
  }

  /**
   * Initialize Email Service (Resend)
   */
  private async initializeEmail(config: any): Promise<void> {
    try {
      if (!config.communications.resend.apiKey) {
        console.warn('  Email (Resend) not configured (optional)');
        return;
      }

      this.initializationStatus.email.initialized = true;
      console.log(' Email service (Resend) configured');
    } catch (error) {
      const msg = error instanceof Error ? error.message : String(error);
      this.initializationStatus.email.error = msg;
      console.warn('  Email service initialization failed:', msg);
    }
  }

  /**
   * Log initialization status
   */
  private logInitializationStatus(): void {
    console.log('\n INITIALIZATION STATUS REPORT');
    console.log('================================\n');

    const status = this.initializationStatus;
    const formatStatus = (s: any) => s.initialized ? '' : ` ${s.error || 'Not configured'}`;

    console.log(`Firebase:    ${formatStatus(status.firebase)}`);
    console.log(`Google AI:   ${formatStatus(status.googleAI)}`);
    console.log(`OpenAI:      ${formatStatus(status.openAI)}`);
    console.log(`Stripe:      ${formatStatus(status.stripe)}`);
    console.log(`NATS:        ${formatStatus(status.nats)}`);
    console.log(`Redis:       ${formatStatus(status.redis)}`);
    console.log(`Email:       ${formatStatus(status.email)}`);

    console.log('\n' + (status.allHealthy 
      ? ' ALL CRITICAL SERVICES INITIALIZED' 
      : ' SOME CRITICAL SERVICES FAILED'));
    console.log('================================\n');

    if (status.allHealthy) {
      captureMessage('Server initialization successful', 'info');
    }
  }

  /**
   * Get Google AI instance
   */
  getGoogleAI(): GoogleGenerativeAI | null {
    return this.googleAI;
  }

  /**
   * Get OpenAI instance
   */
  getOpenAI(): OpenAI | null {
    return this.openAIClient;
  }

  /**
   * Get initialization status
   */
  getStatus(): InitializationStatus {
    return this.initializationStatus;
  }

  /**
   * Health check endpoint
   */
  async healthCheck(): Promise<InitializationStatus> {
    return this.initializationStatus;
  }
}

// Export singleton
export const Initializer = ServerInitializer.getInstance();

