// Create subscription
export async function createSubscription(
  userId: string,
  tierId: keyof typeof SUBSCRIPTION_TIERS,
  customerEmail: string
): Promise<Stripe.Subscription> {
  const tier = SUBSCRIPTION_TIERS[tierId];

  // Get or create Stripe customer
  const customers = await stripe.customers.list({
    email: customerEmail,
    limit: 1,
  });

  let customerId: string;
  if (customers.data.length > 0) {
    customerId = customers.data[0].id;
  } else {
    const customer = await stripe.customers.create({
      email: customerEmail,
      metadata: { userId },
    });
    customerId = customer.id;
  }

  // Create subscription
  const subscription = await stripe.subscriptions.create({
    customer: customerId,
    items: [{ price: tier.stripePriceId }],
    metadata: {
      userId,
      coreTier: tierId,
    },
    payment_behavior: 'default_incomplete',
    expand: ['latest_invoice.payment_intent'],
  });

  // Save to Firestore
  await getDb().collection('subscriptions').doc(userId).set(
    {
      stripeCustomerId: customerId,
      stripeSubscriptionId: subscription.id,
      coreTier: tierId,
      addOns: [],
      status: subscription.status,
      currentPeriodStart: new Date((subscription as any).current_period_start * 1000),
      currentPeriodEnd: new Date((subscription as any).current_period_end * 1000),
      validUntil: new Date((subscription as any).current_period_end * 1000),
      updatedAt: new Date(),
    },
    { merge: true }
  );

  return subscription;
}

// Add addon to subscription
export async function addAddon(
  userId: string,
  addonId: keyof typeof ADDONS
): Promise<Stripe.Subscription> {
  const addon = ADDONS[addonId];
  const subDoc = await getDb().collection('subscriptions').doc(userId).get();

  if (!subDoc.exists) {
    throw new Error('No subscription found');
  }

  const { stripeSubscriptionId } = subDoc.data() as any;

  // Add item to subscription
  const subscription = await stripe.subscriptions.update(stripeSubscriptionId, {
    items: [
      {
        price: addon.stripePriceId,
      },
    ],
  });

  // Update Firestore
  await getDb().collection('subscriptions').doc(userId).update({
    addOns: (await getDb().collection('subscriptions').doc(userId).get()).data()?.addOns || [],
    [`addons.${addonId}`]: true,
  });

  return subscription;
}

// Cancel addon
export async function removeAddon(
  userId: string,
  addonId: keyof typeof ADDONS
): Promise<void> {
  const subDoc = await getDb().collection('subscriptions').doc(userId).get();

  if (!subDoc.exists) {
    throw new Error('No subscription found');
  }

  const { stripeSubscriptionId } = subDoc.data() as any;
  const subscription = await stripe.subscriptions.retrieve(stripeSubscriptionId);

  // Find and remove the addon item
  const addonItem = subscription.items.data.find(
    (item) => item.price.metadata?.addonId === addonId
  );

  if (addonItem) {
    await stripe.subscriptionItems.del(addonItem.id);
  }

  // Update Firestore
  await getDb().collection('subscriptions').doc(userId).update({
    [`addons.${addonId}`]: false,
  });
}

// Get user subscription
export async function getUserSubscription(userId: string) {
  const doc = await getDb().collection('subscriptions').doc(userId).get();

  if (!doc.exists) {
    return null;
  }

  return doc.data();
}

// Handle webhook events
export async function handleStripeWebhook(event: Stripe.Event): Promise<void> {
  switch (event.type) {
    case 'customer.subscription.updated': {
      const subscription = event.data.object as Stripe.Subscription;
      const userId = subscription.metadata?.userId;

      if (userId) {
        await getDb().collection('subscriptions').doc(userId).update({
          status: subscription.status,
          currentPeriodStart: new Date((subscription as any).current_period_start * 1000),
          currentPeriodEnd: new Date((subscription as any).current_period_end * 1000),
          validUntil: new Date((subscription as any).current_period_end * 1000),
          updatedAt: new Date(),
        });
      }
      break;
    }

    case 'customer.subscription.deleted': {
      const subscription = event.data.object as Stripe.Subscription;
      const userId = subscription.metadata?.userId;

      if (userId) {
        await getDb().collection('subscriptions').doc(userId).update({
          status: 'canceled',
          validUntil: new Date(),
          updatedAt: new Date(),
        });
      }
      break;
    }

    case 'invoice.payment_succeeded': {
      const invoice = event.data.object as Stripe.Invoice;
      const customerId = invoice.customer as string;

      // Find user by customer ID and update billing info
      const subs = await getDb()
        .collection('subscriptions')
        .where('stripeCustomerId', '==', customerId)
        .get();

      subs.forEach(async (doc: any) => {
        await doc.ref.update({
          lastPaymentDate: new Date(),
          paymentStatus: 'succeeded',
        });
      });
      break;
    }

    case 'invoice.payment_failed': {
      const invoice = event.data.object as Stripe.Invoice;
      const customerId = invoice.customer as string;

      const subs = await getDb()
        .collection('subscriptions')
        .where('stripeCustomerId', '==', customerId)
        .get();

      subs.forEach((doc: any) => {
        doc.ref.update({
          paymentStatus: 'failed',
          paymentFailedDate: new Date(),
        }).catch(() => {});
      });
      break;
    }
  }
}

// Check if user has addon
export async function hasAddon(userId: string, addonId: keyof typeof ADDONS): Promise<boolean> {
  const sub = await getUserSubscription(userId);
  if (!sub) return false;

  return sub.addons?.[addonId] === true;
}

// Check tier level
export async function getUserTierLevel(userId: string): Promise<string> {
  const sub = await getUserSubscription(userId);
  return sub?.coreTier || 'free';
}

/**
 *  Enhanced Stripe Integration & Billing Management
 * Complete subscription, pricing, and payment management system
 */

import Stripe from 'stripe';
import { getAdminDb } from './firebase-admin';
import { getConfig } from './config';
import { captureException, captureMessage } from './sentry';

let stripeInstance: Stripe | null = null;

export interface StripeProduct {
  id: string;
  name: string;
  description: string;
  tier: 'free' | 'starter' | 'creator' | 'pro' | 'agency' | 'enterprise' | 'education';
  price: number;
  currency: string;
  interval: 'month' | 'year';
  trialDays?: number;
  features: string[];
  limits: Record<string, number>;
  metadata?: Record<string, string>;
}

export interface BillingInfo {
  customerId: string;
  subscriptionId?: string;
  tier: string;
  status: string;
  currentPeriodStart?: Date;
  currentPeriodEnd?: Date;
  cancelAtPeriodEnd?: boolean;
  nextBillingDate?: Date;
  paymentMethod?: string;
}

/**
 * Get Stripe instance
 */
export function getStripe(): Stripe {
  if (!stripeInstance) {
    const config = getConfig();
    if (!config.stripe.secret) {
      throw new Error('STRIPE_SECRET_KEY not configured');
    }
    stripeInstance = new Stripe(config.stripe.secret);
  }
  return stripeInstance;
}

/**
 * Get or create Stripe customer
 */
export async function getOrCreateCustomer(
  userId: string,
  email: string,
  name?: string
): Promise<string> {
  const stripe = getStripe();

  try {
    // Check if customer already exists in Firestore
    const db = getAdminDb();
    if (db) {
      const userDoc = await db.collection('users').doc(userId).get();
      if (userDoc.exists() && userDoc.data()?.stripeCustomerId) {
        return userDoc.data().stripeCustomerId;
      }
    }

    // Search for existing customer by email
    const customers = await stripe.customers.list({ email, limit: 1 });
    if (customers.data.length > 0) {
      const customerId = customers.data[0].id;
      // Save to Firestore
      if (db) {
        await db.collection('users').doc(userId).update({ stripeCustomerId: customerId });
      }
      return customerId;
    }

    // Create new customer
    // const customer removed - unused

    // Save to Firestore
    if (db) {
      await db.collection('users').doc(userId).update({
        stripeCustomerId: customer.id,
      });
    }

    captureMessage(`Created Stripe customer for user ${userId}`, 'info');
    return customer.id;
  } catch (error) {
    console.error('Error managing customer:', error);
    captureException(error, 'stripe_customer_error');
    throw error;
  }
}

/**
 * Create checkout session
 */
export async function createCheckoutSession(
  userId: string,
  email: string,
  priceId: string,
  tier: string,
  options?: {
    successUrl?: string;
    cancelUrl?: string;
    trialDays?: number;
    coupon?: string;
  }
): Promise<Stripe.Checkout.Session> {
  try {
    const stripe = getStripe();
    const customerId = await getOrCreateCustomer(userId, email);

    const sessionData: Stripe.Checkout.SessionCreateParams = {
      customer: customerId,
      line_items: [
        {
          price: priceId,
          quantity: 1,
        },
      ],
      mode: 'subscription',
      success_url: options?.successUrl || `${process.env.NEXT_PUBLIC_BASE_URL}/billing/success?session_id={CHECKOUT_SESSION_ID}`,
      cancel_url: options?.cancelUrl || `${process.env.NEXT_PUBLIC_BASE_URL}/billing/cancel`,
      client_reference_id: userId,
      metadata: {
        userId,
        tier,
        email,
      },
      subscription_data: {
        metadata: {
          userId,
          tier,
        },
      },
    };

    if (options?.trialDays) {
      sessionData.subscription_data!.trial_period_days = options.trialDays;
    }

    if (options?.coupon) {
      sessionData.discounts = [{ coupon: options.coupon }];
    }

    const session = await stripe.checkout.sessions.create(sessionData);
    return session;
  } catch (error) {
    console.error('Checkout session creation error:', error);
    captureException(error, 'stripe_checkout_error');
    throw error;
  }
}

