/**
 * Update subscription
 */
export async function updateSubscription(
  subscriptionId: string,
  options: {
    priceId?: string;
    tier?: string;
    trialEndsAt?: number;
    cancelAtPeriodEnd?: boolean;
  }
): Promise<Stripe.Subscription> {
  try {
    const stripe = getStripe();

    const updateData: Stripe.SubscriptionUpdateParams = {
      metadata: {
        tier: options.tier,
      },
    };

    if (options.priceId) {
      const subscription = await stripe.subscriptions.retrieve(subscriptionId);
      const itemId = subscription.items.data[0]?.id;

      if (itemId) {
        updateData.items = [
          {
            id: itemId,
            price: options.priceId,
          },
        ];
      }
    }

    if (typeof options.cancelAtPeriodEnd === 'boolean') {
      updateData.cancel_at_period_end = options.cancelAtPeriodEnd;
    }

    const updated = await stripe.subscriptions.update(subscriptionId, updateData);
    return updated;
  } catch (error) {
    console.error('Subscription update error:', error);
    captureException(error, 'stripe_update_error');
    throw error;
  }
}

/**
 * Cancel subscription
 */
export async function cancelSubscription(
  subscriptionId: string,
  immediate: boolean = false
): Promise<Stripe.Subscription> {
  try {
    const stripe = getStripe();

    if (immediate) {
      // Immediate cancellation
      return await stripe.subscriptions.cancel(subscriptionId);
    } else {
      // Cancel at period end
      return await stripe.subscriptions.update(subscriptionId, {
        cancel_at_period_end: true,
      });
    }
  } catch (error) {
    console.error('Subscription cancellation error:', error);
    captureException(error, 'stripe_cancel_error');
    throw error;
  }
}

